INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (2,3,11,'Visite cave du château','Visite et initiation à l''art de la décantation dans la cave privée des vieux millésimes de la famille propriétaire.','14:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (3,2,12,'Visite d''une truffière','Visite de la trufficulture avec les explications du trufficulteur','16:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (4,1,16,'Visite de villages jurassiens','Vous visiterez différent villages jurassiens ainsi que le musée de la Maison de la Haute Seille.','14:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (5,1,13,'Visitez la Drôme provençale','La visite suit le parcours du grain de raisin jusqu’à la bouteille de vin pour comprendre les étapes de la création des vins.','10:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (6,1,11,'Visite privée du Château Soutard','Visite des caves voûtées et explications du vigneron','16:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (7,2,15,'Visite de Riquewihr','Visite libre du village de Riquewihr, de sa beauté architecturale et de son site exceptionnel connus dans le monde entier','16:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (8,3,18,'Visite des vignobles','Votre hôte vous indique le parcours à suivre pour une balade autonome dans les vignes. Vous dégustez, de retour au caveau, une sélection de vins du domaine.','08:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (9,1,19,'Visite du domaine viticole','Parcourez la propriété et les caves voûtées où sont élevés leurs vins en barrique. Profitez d''un historique de la maison familiale datant du 16eme siècle avant de déguster les vins du domaine.','14:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (10,4,19,'Visite au Mas Perafita','Découvrir la tradition viticole familiale et les techniques de production du domaine','14:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (11,2,20,'Circuit en Méhari avec un chauffeur','Sillonnez les routes des vignobles autour de Bandol dans une voiture de collection, la Méhari. Au programme : rencontre avec deux vignerons et découverte de lieux chargés d’histoire, tant locale qu’écrite.','16:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (12,1,20,'Visitez le Mas de Cadenet','Vous découvrez un terroir extraordinaire au pied de la montagne Sainte-Victoire, dans un domaine viticole dont l’histoire remonte à 1813.','10:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (13,3,12,'Visitez le du domaine Terre de Mistral','Vous découvrez le chemin surprenant de la terre au verre et les étapes d’élaboration de l’huile d’olive du domaine.','08:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (14,4,25,'Visite et dégustation','Vous serez initiés aux pratiques culturales en agriculture biologique','08:00');
INSERT INTO t_e_visite_vst (vst_id,tvs_id,cav_id,vst_libelle,vst_description,vst_horaire) VALUES (15,4,18,'Visite du Domaine Henri Giraud','Une occasion unique de vous immerger dans le terroir, l’histoire et la culture du Domaine. Vous découvrirez la particularité de la Maison Henri Giraud : une vinification naturelle et créative','10:00');
