INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (1,1,10,'Jour 1 - Dégustation des grands vins de la Côte-de-Beaune','A votre arrivée à Puligny-Montrachet, au cœur de la Bourgogne des grands vins, vous avez rendez-vous à la Maison Olivier Leflaive pour une visite de la propriété suivie d''un déjeuner à la table d''hôte du domaine avec dégustation commentée des vins Olivier LeflaiveL''après-midi se poursuit le long de la route des Grands Crus entre Santenay et Beaune, sur la fameuse Côte des blancs. Vous ferez une halte à la Maison Prosper Maufoux au Château de Saint-Aubin. Cette visite suivie d''une dégustation sera l''occasion de vous immerger dans l''univers des vignerons bourguignonsRetour à Puligny-Montrachet et diner gastronomique au restaurant Le Montrachet - hors boissonNuitée à la Maison Olivier Leflaive','https://www.vinotrip.com/img/cms/Balade-dans-les-vignes-en-Bourgogne-Olivier-Leflaive.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (2,1,2,'Jour 2 - Découverte d''Aloxe-Corton','Petit déjeunerVous poursuivez vos dégustations de vins réputés en Bourgogne avec un rendez-vous dans la matinée au magnifique Château de Corton-André à Aloxe-Corton, pour une dégustation des vins Pierre AndréEn option - Vous êtes conviés à la table d''hôtes Comte Senard à un déjeuner dégustation des vins d''appellation de la colline de Corton, en compagnie des sommeliers de la maisonAprès-midi libre à la découverte de Beaune, de ses hospices, ...','https://www.vinotrip.com/img/cms/Ch%C3%A2teau-Pierre-Andr%C3%A9.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (3,2,4,'Jour 1 - Arrivée en plein coeur du vignoble','A votre arrivée en Bourgogne, vous vous installez à la Maison Rouge, chambre d’hôtes de charme située à quelques kilomètres de Beaune, à deux pas des vignobles de la Côte de Nuits, point de départ idéal pour une découverte de l''oenotourisme en BourgogneEn option - Dîner au restaurant La Gremelle proposant une cuisine gourmande du terroir - Menu hors boissonNuitée à la Maison Rouge','https://www.vinotrip.com/img/cms/H%C3%A9bergement%20vignoble%20bourgogne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (4,2,15,'Jour 2 - Découvrez les secrets du vin et déjeunez chez le vigneron','Après un copieux petit déjeuner, vous participez à un atelier oenologique et une balade découverte de la vigne et du vin au Clos de Bourgogne, en compagnie d''une oenologue diplômée, au départ de Gevrey-ChambertinPour récupérer de votre promenade matinale, vous déjeunez à la table d''hôte de la maison Trapet, à Gevrey-Chambertin. Confortablement installé à côté de la cheminée, Damien vous accompagnera tout au long de votre dégustation de 4 vinsL’après-midi se poursuit le long de la route des vins en Bourgogne, à Nuit Saint Georges, par une rencontre suivie d’une dégustation avec un vigneron au parcours atypique, Manuel Olivier qui a commencé il y a 20 ans à cultiver des fruits dans les Hautes Côtes de NuitsEn option - Dîner gastronomique au restaurant Christian Quenel - Menu hors boissonNuitée à la Maison Rouge','https://www.vinotrip.com/img/cms/clos-saint-louis-caveau.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (5,2,2,'Jour 3 - Dégustez la truffe de Bourgogne','Petit déjeunerEn fin de matinée, vous visiterez une truffière et découvrirez les secrets du cavageLa visite se termine par un déjeuner autour de la truffe accompagné d''un verre de vin','https://www.vinotrip.com/img/cms/visite-truffiere-bourgogne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (6,3,5,'Jour 1 - Atelier oenologique dans le bordelais','A votre arrivée sur la route des vins bordelais en matinée, direction le Château du Taillan, Cru Bourgeois du Haut-Médoc pour une visite commentée de la propriété et une initiation à l''art de la décantation dans la cave privée des vieux millésimes de la famille propriétaire. La visite est suivie d''une dégustation gourmande des vins du château accompagnés de fromages et fruits secsDéjeuner dans un restaurant à Margaux - Menu hors boissonVous prenez ensuite la route du vignoble de l''Entre-deux-Mers où vous avez rendez-vous dans un domaine viticole pour une visite d''un Château suivie d''une dégustation de ses grands vinsDiner à proximité de votre hébergement - Menu hors boissonNuit dans un superbe domaine viticole, et chambre d’hôtes, situé en Entre-deux-Mers','https://www.vinotrip.com/img/cms/visite_chateau_du_taillan_1.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (7,3,1,'Jour 2\xa0- A la découverte des grands vins de Bordeaux','Petit déjeunerRendez-vous dans au Château Tour Saint Christophe à Saint-Emilion pour une immersion œnologique au cours d''une visite complète du domaine (tour du vignoble, visite du cuvier, du chais à barrique et d''élevage) et d''une dégustation des Grands Crus du ChâteauEn option - Vous pouvez compléter votre tour des vignobles bordelais en participant à une activité œnologique complémentaire au choix : un atelier assemblage au Château de Camarsac, un atelier découverte des arômes du Sauternes au Château Suduiraut ou une visite / dégustation au Château Bouscaut à la\xa0 découverte des grands vins de Pessac-Leognan','https://www.vinotrip.com/img/cms/Bordeaux%20-%20Séjour%20-%20Oenologie%20(11).jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (8,4,11,'Jour 1 - Arrivée sur les terres des Grands Vins Blancs de Bourgogne','A votre arrivée à Puligny-Montrachet, vous avez rendez-vous à la Maison Olivier Leflaive pour une dégustation commentée de trois vins du domaineDéjeuner à la table de la Maison Olivier Leflaive (en option)En fin d''après-midi, vous vous installez dans votre magnifique hébergement, L''Hostellerie de Levernois, hôtel 5* niché dans un parc arboré de 5 hectares. Cette maison bourgeoise est élevée au rang de Relais & ChâteauxVous profitez d''un moment de détente à l''heure du thé : un soin de Fruitithérapie au spa du Château de Citeaux à Meursault (en option)Dîner gastronomique au restaurant de L''Hostellerie de Levernois, 1* au guide Michelin - hors boissonNuitée à L''Hostellerie de Levernois - hôtel 5 étoiles','https://www.vinotrip.com/img/cms/Jour%201.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (9,4,9,'Jour 2 - Dégustation de Grands Crus au domaine','Petit déjeunerRendez-vous à Aloxe-Corton en fin de matinée pour une dégustation des vins du Château Corton CMatinée alternative (en option) : découvrez le vignoble de Bourgogne vu du cielVisite libre de Beaune','https://www.vinotrip.com/img/cms/Jour%202.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (10,5,11,'Jour 1 sur la route des vins en Bourgogne','A votre arrivée à Puligny-Montrachet, au cœur de la Bourgogne des grands vins, vous avez rendez-vous à la Maison Olivier Leflaive pour une visite de la propriété suivie d’un déjeuner à la table d’hôte du domaine avec dégustation commentée des vins Olivier LeflaiveSur la route des Grands Crus, vous ferez une halte chez un vigneron entre Santenay et Meursault. Cette dégustation des vins de la propriété sera l’occasion de vous immerger dans l’univers des vignerons bourguignonsEn option - Diner au restaurant Le Terroir à SantenayNuitée dans une des chambres d’hôtes de la Maison Prosper Maufoux au Château de Saint-Aubin','https://www.vinotrip.com/img/cms/sejour-chateau-saint-aubin-bourgogne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (11,5,11,'Jour 2 à la découverte de Meursault et Beaune','Petit déjeunerEn option - Direction Meursault où vous serez initiés à l’art de la tonnellerie de manière vivante et convivialeVous êtes attendus à Beaune pour une visite de la cave du Domaine Debray, suivie d''une dégustation commentée des vins de la propriétéVotre séjour sur la route des vins de Bourgogne se termine par la découverte de Beaune : visite de la ville et des hospices (entrées non incluses)','https://www.vinotrip.com/img/cms/Atelier%20Tonnellerie.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (12,6,10,'Jour 1 - Installation dans votre cabane dans le vignoble bordelais','Votre séjour débute par la visite d''une production artisanale de safran, l''or rouge aux mille vertus - si activité non disponible, bouteille offerteEn fin de journée, vous prenez la direction de Pompignac, où vous êtes attendus au Château La Romaningue pour une aventure insolite au coeur du vignoble : vous séjournerez dans une cabane perchée sur un chêne bicentenaire, à 9 mètres entre bois, champs et vignesDîner dans votre cabane : paniers chauds à déguster sur la terrasse de votre cabane et préparés par le chef d''un restaurant régional, accompagnés d''une bouteille de Château La RomaningueNuitée dans votre cabane','https://www.vinotrip.com/img/cms/hebergement-cabane-bordelais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (13,6,6,'Jour 2 - Balade à vélo et découverte des vins biologiques de Saint-Emilion','Petit-déjeuner à hisser jusqu’à votre cabane à l’aide d’une corde poulieDirection Saint-Emilion pour une journée à la découverte de cette cité classé au patrimoine mondial de l’UNESCOVous récupérez votre vélo pour une pérégrination dans le vignoble de Saint-EmilionPour le déjeuner, direction le Château Soutard, Grand Cru Classé de Saint-Emilion, pour une pause gourmande : profitez d’un cadre magnifique pour savourer un pique-nique champêtre en toute intimité et dégustez des produits régionaux (un verre de vin par personne inclus)En option - Vous poursuivez votre découverte du Château Soutard par une visite privée exclusive suivie d''une dégustation des vins de la propriété, produits sous la certification Haute Valeur EnvironnementaleDans l''après-midi, vous parcourez le Château Tour Saint-Christophe au cours d''une visite du vignoble, du cuvier et des chais et dégustez trois grands vins du domaine','https://www.vinotrip.com/img/cms/balade-velo-vignoble-saint-emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (14,7,7,'Architecture et vinification','A votre arrivée dans le Médoc, vous prenez la direction du Château du Taillan, magnifique propriété familiale datant du XVIIème siècle, d’architecture classique, abritant des caves souterraines répertoriées aux Monuments Historiques de France.Vous serez pendant 1h30 en immersion totale dans l’environnement d’un vigneron, découvrant ainsi tous les secrets de fabrication des grands vins du Médoc.La visite est suivie d’une dégustation des vins du Château.','https://www.vinotrip.com/img/cms/visite-medoc-taillan.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (15,7,11,'Promenade et dégustation','Votre journée sur les chemins du Médoc se poursuit au Château Saint Ahon par une visite ludique au cœur du vignoble du domaine. Cette promenade libre "Les Jardins de Mirabel" invite les visiteurs à découvrir la nature, la vigne, la faune et la flore de la région qui environnent le viticulteur, grâce à des jeux quiz et des panneaux explicatifs.La visite se termine par une dégustation (vins pour les grands / jus de raisins pour les plus jeunes).','https://www.vinotrip.com/img/cms/Visite%20du%20M%C3%A9doc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (16,7,7,'Déjeuner et visite d''un Grand Cru Classé','Pause déjeuner au restaurant le 1902, au cœur d''une tonnellerie. Les produits du terroir sont à l''honneur, et les recettes locales revisitées ! (Menu hors boisson).Le Château Desmirail, 3ème Grand Cru Classé en 1855 de l’appellation Margaux, vous accueille ensuite pour une découverte des cuviers et des chais suivie d’une dégustation des vins du Château.Hébergement (avec petit-déjeuner) en option.','https://www.vinotrip.com/img/cms/visite-grand-cru-classe-medoc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (17,8,12,'Jour 1 - Arrivée au coeur du vignoble bourguignon','A votre arrivée en Bourgogne, vous empruntez la route des Grands Crus en direction de Beaune. Vous vous installez dans votre chambre au sein de l''Hostellerie Le Cèdre, magnifique hôtel 5* situé dans un écrin de verdure aux arbres centenairesEn fin d''après-midi, profitez d''une visite et dégustation chez l''un de nos vignerons partenaires dans la ville de BeauneEn option - Dîner au Bistrot du Bord de l''Eau, restaurant au cadre chaleureux situé dans les anciennes cuisines d''une bâtisse historique datant de 1750 - Menu hors boissonNuitée à l''Hostellerie Le Cèdre','https://www.vinotrip.com/img/cms/hostellerie-le-cedre.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (18,8,7,'Jour 2 - Golf','Petit déjeuner à l''hôtelDépart pour le Golf du Château de Chailly, à Pouilly en Auxois et déjeuner au restaurant Le RubillonParcours 18 trous, sur le parcours conçu par Thierry Sprecher et Géry Watine, champion de France professionnel en 1989En option - Retour à Beaune pour un dîner gastronomique au restaurant de votre hôtel, Le Clos du Cèdre - Menu dégustation en 4 plats, hors boissonNuitée à l''Hostellerie Le Cèdre','https://www.vinotrip.com/img/cms/golf-de-chailly-bourgogne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (19,8,9,'Jour 3 - Dégustation de vins','Petit déjeuner à l''hôtelVous empruntez la route des Grands Crus et sillonnez le vignoble en direction d''Aloxe-Corton, au Château Corton C. pour une dégustation des vins du domaineEn option - Vous êtes conviés à la table d''hôte Comte Senard à un déjeuner dégustation des vins d''appellation de la colline de Corton, en compagnie des sommeliers de la maisonAprès-midi libre à Beaune pour une visite du cœur de la ville','https://www.vinotrip.com/img/cms/Domaine%20Pierre%20André.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (20,9,10,'Jour 1 - Découverte du vignoble alsacien','A votre arrivée en Alsace, vous êtes attendus à Hunawihr, où vous avez rendez-vous pour une visite du domaine François Schwach et une dégustation de leurs spécialités de Crémants d’AlsaceAprès votre dégustation, vous vous installez à la maison d''hôtes Demeure d''Antan, un ancien domaine viticole datant du 18eme siècle situé entre Riquewihr et ColmarNuitée à la maison d''hôtes Demeure d''Antan','https://www.vinotrip.com/img/cms/Jour-1-gite.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (21,9,3,'Jour 2 - Atelier vignerons sur les routes d''Alsace','Petit déjeunerMatinée en immersion au Domaine Schoenheitz, vous y êtes attendus pour une visite de la cave suivie d''une dégustation gastronomique commentée par le vigneron : dégustation d''une sélection de 5 vins, Kougelhopf et Munster fermier au lait cruEn option - Déjeuner gastronomique à l''Auberge du Vieux Pressoir, restaurant traditionnel du domaine du Bollenberg, boissons comprisesAprès le déjeuner, direction Beblenheim, vous avez rendez-vous au domaine Bott-Geyl pour une dégustation de leurs magnifiques Grands Crus cultivés en biodynamie : Sonnenglanz, Furstentum, Schoenenbourg, Mandelberg, …Diner dans un restaurant traditionnel qui vous proposera un menu typique de la gastronomie alsacienne en accord mets et vinsNuitée à la maison d''hôtes Demeure d''Antan','https://www.vinotrip.com/img/cms/Travail-dans-les-vignes.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (22,9,5,'Jour 3 - Découverte des villages alsaciens','Petit déjeunerDans la matinée, vous partez à la découverte de Saint-Hippolyte, magnifique village alsacien avec ses maisons à colombages séparées par des venellesDéjeuner au Rabseppi-Stebel, restaurant typiquement alsacien avec vue sur le vignoble, hors boisson (en option)','https://www.vinotrip.com/img/cms/Jour-3-Vigneron-Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (23,10,14,'Jour 1 - Arrivée au château et découverte de Margaux','A votre arrivée à Bordeaux, vous empruntez la route des Châteaux en direction de MargauxVous êtes attendus dans l’après midi au Château du Tertre, 5ème Grand Cru Classé de Margaux, où nous vous avons réservé une des fabuleuses chambres avec une vue à couper le souffle sur le vignobleAprès vous être installé, vous découvrez le domaine et son terroir, et dégustez les vins du Château du TertreDiner semi-gastronomique à Margaux en option (menu hors boisson)Nuitée au Château du Tertre','https://www.vinotrip.com/img/cms/Ch%C3%A2teau%20du%20Tertre.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (24,10,9,'Jour 2 - Golf et dégustation de Grands Crus bordelais','Petit déjeuner au ChâteauDépart pour le Golf du Médoc et déjeuner au restaurant Le Club au Golf du MédocParcours "Les Châteaux" (le green fee peut-être remplacé par un soin au Spa Décleor), ce parcours de championnat, dessiné par l’architecte américain Bill Coore s’inscrit dans la plus pure tradition des links écossaisRetour à Margaux en fin de journée pour une visite et dégustation au Château Giscours, 3ème Grand Cru Classé de MargauxNuitée au Château du Tertre','https://www.vinotrip.com/img/cms/Golf%20du%20M%C3%A9doc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (25,10,7,'Jour 3 - Château et golf','Petit déjeuner au ChâteauDernière étape de votre week-end vin et golf à Bordeaux en option : parcours 18 trous au Golf de Lacanau','https://www.vinotrip.com/img/cms/Golf.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (26,11,1,'Jour 1 - Balade en vélo et dégustations dans le vignoble champenois','A votre arrivée en Champagne, vous prenez la direction d''Epernay où votre guide vous attend pour débuter une balade d’une demi-journée en VTT au cœur du vignoble champenois. Selon votre niveau (et votre motivation !), votre guide s’adaptera et vous fera découvrir les plus beaux points de vue de la région. Une halte dégustation chez un vigneron champenois ainsi qu’un déjeuner clôtureront cette petite escapade sportive et gourmandeEn option - Vous vous dirigez ensuite à Boursault où vous êtes attendus à la Maison Le Gallais pour vivre une "masterclass 100% vin" : présentation des étapes de la vinification, visite des caves et démonstration de dégorgement à la volée, clôturée par une dégustation de 3 cuvées de la maisonEn fin d’après-midi, vous rejoignez votre hébergement à quelques kilomètres au nord d’Epernay, la maison d''hôtes du Champagne Philippe Martin, charmante chambre d’hôtes située dans le village de CumièresDiner au Bistrot le 7 autour d''une cuisine de marché - Menu hors boissonNuitée à la chambre d''hôtes du Champagne Philippe Martin','https://www.vinotrip.com/img/cms/J1%20-%20Escapade%20viticole.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (27,11,7,'Jour 2 - Escapade en 2CV dans les vignes de Champagne','Petit-déjeuner à votre hébergementVous participez au Domaine Philippe Martin, propriété familiale depuis 1892, à une balade inédite en 2 CV avec les propriétaires du domaine. Une expérience inoubliable qui vous offrira des vues imprenables sur la Vallée de la Marne et vous permettra de partager, le temps d’une escapade, la passion des professionnels de la vigne et du vin. En point d’orgue de la balade, vous bénéficierez d’une dégustation de leurs meilleures cuvéesEn option - Déjeuner dans un restaurant en centre ville d''Epernay autour d''une cuisine généreuse et de terroir – Menu entrée, plat, dessert, hors boissonVous prenez ensuite la direction du Domaine de Castellane pour une visite guidée du domaine, du musée et de sa célèbre tour panoramique de 60m de haut qui vous offrira une vue imprenable sur la ville. La visite sera suivie d’une dégustation des champagnes du domaine','https://www.vinotrip.com/img/cms/J2%20-%20Escapade%20viticole.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (28,12,10,'Jour 1 de votre week-end oenologie à Saint-Emilion','A votre arrivée à Saint-Emilion, où vous êtes attendus au Château de Môle par les propriétaires et viticulteurs pour une visite de la propriété suivie d''une dégustation commentée des vins du domaineEn option - Dîner dans un restaurant à proximité du ChâteauNuit au Château de Môle','https://www.vinotrip.com/img/cms/we-chateau-saint-emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (29,12,7,'Jour 2 - Atelier vigneron sur la route des vins de Bordeaux','Petit déjeunerEn matinée, départ pour Saint-Emilion, à la sortie du village vous empruntez une petite route sinueuse qui monte jusqu''au Château de Ferrand, Grand Cru Classé depuis 2012Après une visite du vignoble et des chais, vous découvrirez les secrets des Grands Vins de la rive droite au cours d''un atelier assemblageL''atelier se termine par une dégustation des vins de la propriété accompagnés d''une planche de fromages et de charcuterie sélectionnés avec soinEn option - Dîner gastronomique à l''Auberge Saint Jean : menu dégustation selon l''humeur du chef (hors boisson)Nuit au Château de Môle','https://www.vinotrip.com/img/cms/Bordeaux%20-%20S%C3%A9jour%20-%20Oenologie%20(5).jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (30,12,2,'Jour 3 - Découverte de Saint-Emilion','Petit déjeunerVotre week-end oenologie se termine par une journée libre à Saint-Emilion, cité médiévale classée au Patrimoine Mondial de l''UnescoEn option - Déjeuner à L''Envers du décor, bar à vins au coeur de Saint-Emilion (hors boisson)','https://www.vinotrip.com/img/cms/Aquitaine%20-%20saint-emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (31,13,7,'Jour 1 - Arrivée au coeur du vignoble alsacien','Arrivée à Kaysersberg, ville impériale depuis 1293 et ville de la Décapole depuis sa création en 1354 à l’exceptionnelle richesse patrimonialeInstallation au Chambard, hôtel 5*Découverte du village, de ses ruelles pavées et de ses maisons à colombages nichées entre vignes et montage, dans le dernier étranglement de la vallée de la Weiss avant l’ouverture sur la plaineDîner à la winstub du Chambard, bistrot traditionnel - hors boisson : carreaux rouge et blanc, mobilier de bois clair, verres à vin à haut pied vert, et plats typiques de la gastronomie alsacienne revisités par le chef étoilé Olivier Nasti : choucroute, backaoffa, escargots, munster, ...Nuit au Chambard','https://www.vinotrip.com/img/cms/Le-Chambard-en-Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (32,13,2,'Jour 2 - Golf et Restaurant étoilé','Petit déjeuner buffetDépart pour le Golf d’Ammerschwihr pour un parcours 18 trous. Le green fee peut être remplacé par un soin à la carte au spa de l’hôtelVous empruntez ensuite la route des vins en direction de Beblenheim, vous avez rendez-vous au domaine Bott-Geyl pour une visite et une dégustation de leurs magnifiques Grands Crus cultivés en biodynamie : Sonnenglanz, Furstentum, Schoenenbourg, Mandelberg, …En option - Retour à Kaysersberg et diner gastronomique au restaurant 2* au Guide Michelin du Chambard, hors boissonNuit au Chambard','https://www.vinotrip.com/img/cms/Golf%20d%E2%80%99Ammerschwihreen%20Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (33,13,15,'Jour 3 - Dégustation de Grands Crus','Petit déjeuner buffetMatinée libre, vous profitez du spa de l''hôtel (soins non inclus) ou partez à la découverte de la région et des magnifiques villages le long de la route des vinsDirection Wettolsheim : vous avez rendez-vous pour une visite et une dégustation des vins du domaine Stentz-Buecher où vous serez initiés aux pratiques culturales en agriculture biologique','https://www.vinotrip.com/img/cms/cave-domaine-viticole-alsacien.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (34,14,13,'Jour 1 - Vendanges dans le vignoble alsacien','A votre arrivée en Alsace, vous vous rendez chez l’un de nos vignerons partenaires pour une matinée de découverte à la vigne en compagnie du vigneron qui vous fera partager sa passion. Explications sur les techniques de vendanges, découverte des gestes propres à une vendange de qualité… Vous percerez tous les secrets de l’élaboration des Grands Vins d’Alsace ! Le repas du vendangeur ainsi qu’une dégustation clôtureront cette matinée de partage au cœur du vignoble.Après le déjeuner, direction Beblenheim, vous avez rendez-vous au domaine Bott-Geyl pour une dégustation de leurs magnifiques Grands Crus cultivés en biodynamie : Sonnenglanz, Furstentum, Schoenenbourg, Mandelberg, …Dîner dans un restaurant typiquement alsacien proposant une cuisine traditionnelle et gourmande.Nuitée à la Demeure d''Antan, maison d''hôte de charme située au cœur du vignoble.','https://www.vinotrip.com/img/cms/Week-end%20vendanges%20Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (35,14,13,'Jour 2 - Découverte du patrimoine','Petit déjeuner.Dans la matinée, direction Wettolsheim, vous avez rendez-vous pour une dégustation des vins du domaine Stentz-Buecher où vous serez initiés aux pratiques culturales en agriculture biologique.Votre séjour se termine par la découverte de Colmar en barque le long de la Lauch, une façon insolite et écologique de visiter la ville.','https://www.vinotrip.com/img/cms/Activités%20vendanges%20Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (36,15,14,'Un stage oenologique au coeur des vignobles','Ce coffret cadeau comprend une activité œnologique chez l’un de nos partenaires à Bordeaux, en Bourgogne, en Champagne, en Alsace ou en Val de Loire. Vous pourrez, au choix, profiter d''un atelier assemblage, d''une initiation à la dégustation, de la création de son Grand Cru, … Autant d’activités qui vous permettront de découvrir le vin et l''oenologie !','https://www.vinotrip.com/img/cms/stage-oenologie.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (37,15,12,'Un cadeau unique','Choisissez votre destination et immergez-vous le temps d’une demi-journée dans l’univers des grands vins. Apprenez l’importance des terroirs dans l’élaboration d’un vin, découvrez les cépages caractéristiques des régions, percez les secrets des techniques de vinification, initiez-vous aux assemblages et apprenez l’art de la dégustation. Le vin n’aura plus de secret pour vous.','https://www.vinotrip.com/img/cms/stage-vin.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (38,15,1,'Informations','Le coffret cadeau est utilisable pendant 18 mois parmi les partenaires sélectionnés par nos soins dans les vignobles. Notre équipe est à votre disposition pour organiser le stage à la date choisie, nous nous occupons de tout !','https://www.vinotrip.com/img/cms/stage-degustation.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (39,16,9,'Jour 1 - Vivez la vie de Château','A votre arrivée à Bordeaux, direction Puisseguin, satellite de Saint-Emilion, où vous vous installez au Château de Môle, propriété viticole et magnifique maison d’hôtesVos hôtes vous attendent pour une visite de la propriété suivie d’une dégustation des vins\xa0Dîner gastronomique à l''Auberge Saint Jean : menu dégustation selon l''humeur du chef (hors boisson)Nuit au Château de Môle','https://www.vinotrip.com/img/cms/clos-barbanne-bordeaux.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (40,16,12,'Jour 2 - Profitez d''un atelier oenologique dans un Château bordelais','Après un délicieux petit déjeuner à votre hébergement, vous prenez la route de Néac, tout près de Pomerol, où vous êtes attendus dans la matinée au Château Siaurac pour une visite privée du domaine et des somptueux jardinsNous vous avons réservé un atelier de dégustations avec planche de fromages pour goûter aux Grands Vins de la rive droite et découvrir les accords fabuleux avec les fromages sélectionnésDéjeuner en accord mets et vins à L''Envers du DécorAprès-midi libre à Saint Emilion, cité médiévale classée au patrimoine mondial de l’UNESCOEn option - Dîner gastronomique au Clos Mirande, au coeur des vignes de Saint-Emilion (menu hors boisson)Retour et nuit au Château de Môle','https://www.vinotrip.com/img/cms/romance-chateau-bordelais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (41,16,1,'Jour 3 - Relaxez-vous au coeur du vignoble bordelais','Petit déjeuner Vous profitez du sauna pour vous détendre avant votre départEn option - Dernières étapes de votre séjour :- visite privée du Château Soutard et dégustation des vins de la propriété- 35min de soin au spa du Château Grand Barrail à Saint-Emilion- survol du vignoble de Saint-Emilion et apéritif au crémant de Bordeaux produit par le pilote viticulteur au Château Les 3 Roses. Le vol en montgolfière est clôturé par un pique-nique champêtre avec dégustation des vins du Château Les 3 Roses','https://www.vinotrip.com/img/cms/Bordeaux%20-%20S%C3%A9jour%20-%20Oenologie%20(19).jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (42,17,13,'Jour 1 - Découverte du Chenin blanc sec et des troglodytes','Plongez au cœur de l’histoire viticole angevine et percez les secrets de la vinification au travers d’une visite du domaine FL (Fournier-Longchamps) et de ses installations : chai, cuverie, pressoirs, vignes, … Votre visite se termine par une dégustation commentée des vins bios et biodynamiques du domaine FLVous poursuivez votre tour des domaines viticoles en Anjou par une visite du Château Soucherie, actuellement en conversion bio, qui s’attache à remettre la biodiversité au cœur des vignes. Le domaine cherche par la plantation de champ de fleurs et céréales, l’implantation de ruches et l’éco-pâturage à développer une viticulture responsable et respectueuse de l’environnement qui lui offre de si beaux fruitsVous vous installez ensuite à votre hébergement insolite creusé dans les fameux troglodytes qui font la renommée de la régionDîner au restaurant de l’hôtel, dans les troglodytes, où vous gouterez la spécialité régionale : les fouées - Menu avec boissons comprisesNuit à l''hôtel dans une chambre double','https://www.vinotrip.com/img/cms/sejour-bio-anjou.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (43,17,3,'Jour 2 - Dégustations des vins de Savennières et visite d’Angers','Petit déjeuner à votre hébergementVisite autonome du musée Troglo du village troglodyte de Rochemenier où vous partirez à la découverte d''un monde souterrainEn option - Déjeuner au restaurant Le Chenin à Savennières proposant des produits locaux de saison issus de l’agriculture biologiqueRendez-vous dans l’après-midi à Savennières au Château de Vaults où vous découvrirez lors d’une promenade commentée dans le parc et le vignoble, le paysage de Savennières, les terroirs et l’approche naturelle de la culture de la vigne par le domaine. Vous prendrez ensuite place dans l’un des salons du Château pour une dégustation de 5 vins en accord avec des fromages affinésVous terminez votre escapade en Anjou par la découverte de la magnifique ville d’Angers, où se trouve notamment le fameux château médiéval comprenant la plus grande tapisserie au monde, l''Apocalypse','https://www.vinotrip.com/img/cms/degustation-vin-fromage-loire.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (44,18,5,'Jour 1 - Atelier oenologique en Champagne','A votre arrivée pour votre week-end en Champagne, direction Vrigny, village champenois typique proche de Reims, où vous vous installez confortablement dans une des chambres d’hôtes du domaine Roger CoulonVous passez l’après-midi à la propriété en immersion totale et découvrez le travail du vigneron au cours d''un atelier au choix dont la thématique varie en fonction des dates du séjour :– Initiation à la dégustation de Champagne (toute l''année) : apprenez à faire travailler vos sens, les techniques de la dégustation et dégustez plusieurs Champagne du plus traditionnel au plus atypique– Travail dans les vignes (de mai à juillet, selon disponibilités) : en compagnie de toute l’équipe du domaine, vous participez aux travaux de la vigne : ébourgeonnage, palissage, …Dîner en option au restaurant bistronomique Au Petit comptoir à Reims - Menu entrée, plat, dessert, 3 verres de vins inclusNuitée au domaine Roger Coulon','https://www.vinotrip.com/img/cms/domaine-champagne-roger-coulon.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (45,18,14,'Jour 2 - Visite des caves d''une grande maison champenoise','Petit déjeuner au domaine Roger CoulonRendez-vous dans la matinée au domaine Vranken Pommery pour une visite des caves d''une grande maison champenoise suivie d’une coupe de champagne du domaineVous déjeunez ensuite au cœur du quartier des Halles du Boulingrin autour d''un menu gourmand (entrée, plat, dessert - hors boisson)Visite libre de Reims','https://www.vinotrip.com/img/cms/cave-champagne-pommery.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (46,19,10,'Jour 1 - Découverte des vins biologiques de la Drôme Provençale','A votre arrivée dans la région à Suze-la-Rousse, village drômois connu pour sa forteresse médiévale, vous avez rendez-vous chez l''un de nos vignerons partenaires pour une visite de la propriété suivie d’une dégustation de ses vins biologiques.Vous déjeunez à Vaison-la-Romaine chez O’Natur’Elles, restaurant biologique, qui vous propose une cuisine variée à base de produits frais - hors boisson.Dans l’après-midi, vous reprenez la route vers un domaine travaillant en cosmoculture (certifié biologique). Après une visite de la propriété, remarquable tant par son architecture que par les vins biologiques et biodynamiques qui y sont produits, vous dégustez les vins de la maison.Vous vous installez ensuite dans l''un de nos hébergements partenaire situé à quelques minutes de Suze-la-Rousse.Pour le dîner, vous savourez une cuisine provençale locale – Menu hors boisson.Nuitée.','https://www.vinotrip.com/img/cms/vaison-la-romaine.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (47,19,14,'Jour 2 - Sur les chemins des vignobles à vélo','Petit déjeuner.C’est le moment de découvrir les environs au cours d’une balade à vélo électrique, durant la matinée. Admirez les paysages chatoyants de Provence, les cours d’eau, et les villages médiévaux éparpillés au pied du Mont Ventoux.','https://www.vinotrip.com/img/cms/suze-la-rousse-drome.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (48,20,7,'Jour 1 - Alliance gastronomie et champagne','A votre arrivée en Champagne, vous êtes attendus au Domaine Philippe Martin, propriété familiale depuis 1892, pour une découverte de leur savoir-faire traditionnel suivie d''une dégustation des meilleures cuvées de la maisonVous vous dirigez ensuite vers la Vallée de la Marne, à Boursault précisément où vous êtes attendus à la Maison Le Gallais pour vivre une "masterclass 100% vin" : présentation des étapes de la vinification, visite des caves et démonstration de dégorgement à la volée, clôturée par une dégustation de 3 cuvées de la maisonEn fin d’après midi, vous vous installez confortablement à Epernay dans une des magnifiques chambre de l''hôtel 4* Jean MöetEn option - Dîner au restaurant Bistrot le 7 autour d''une cuisine de saison et des produits du marché - Menu hors boissonNuitée à l''hôtel Jean Möet','https://www.vinotrip.com/img/cms/Champagne%20Epernay.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (49,20,6,'Jour 2 - Déjeuner au coeur du vignoble champenois','Petit déjeuner à l''hôtelEn option - Visite des caves de la Maison Mercier suivie d''une dégustation des champagnes Mercier Brut, Brut Rosé et RéserveDéjeuner dans un restaurant à Epernay - hors boissonVisite libre d’Epernay : ne manquez pas la fameuse Avenue de Champagne où se succèdent les prestigieuses Maisons de champagneEn option - Sillonnez le vignoble champenois à vélo en compagnie d''un guide et découvrez les plus beaux points de vue de la région','https://www.vinotrip.com/img/cms/vignoble_champagne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (50,21,9,'Départ de Bordeaux, direction Saint-Emilion !','Rendez-vous à 13h50 devant l''Office de tourisme de Bordeaux où vous retrouvez votre guide','https://www.vinotrip.com/img/cms/vue-saint-emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (51,21,15,'Visite d’un Château et promenade guidée à Saint-Emilion','Vous débutez votre tour par une visite d''un château Grand Cru ou Grand Cru Classé. La visite se termine par une dégustation des vins de la propriété','https://www.vinotrip.com/img/cms/tour-saint-emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (52,21,1,'Visite d''une propriété familiale et retour à Bordeaux','Vous visitez une propriété familiale : à l''issue de votre visite, un apéritif gourmand accompagnera votre dégustation de vins','https://www.vinotrip.com/img/cms/aperitif-gourmand-saint-emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (53,22,10,'1ère journée de votre week-end gastronomique en Alsace','Arrivée à Kaysersberg, ville impériale depuis 1293 et ville de la Décapole depuis sa création en 1354 à l’exceptionnelle richesse patrimonialeInstallation au Chambard, hôtel 5* idéal pour votre escapade gourmande dans le vignoble alsacienDécouverte du village, de ses ruelles pavées et de ses maisons à colombages nichées entre vignes et montage, dans le dernier étranglement de la vallée de la Weiss avant l’ouverture sur la plaineDîner au restaurant Flamme & co, spécialités de tartes flammées - hors boissonNuit au Chambard','https://www.vinotrip.com/img/cms/Le-Chambard-en-Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (54,22,1,'2ème journée à la découverte des vins et mets alsaciens','Petit déjeuner buffetVous empruntez la route des vins pour vous rendre entre Colmar et Munster au domaine Schoenheitz. Vous y êtes attendus pour une visite de la cave suivie d''une dégustation gastronomique commentée par le vigneron : dégustation d''une sélection de 5 vins, Kougelhopf et Munster fermier au lait cruDépart pour Hunawihr, où vous avez rendez-vous pour une visite du domaine François Schwach et une dégustation de leurs spécialités de Crémants d’AlsaceEn option - Retour à Kaysersberg et diner gastronomique au restaurant 2* au Guide Michelin du Chambard, hors boissonNuit au Chambard','https://www.vinotrip.com/img/cms/Kaysenberg-en-Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (55,22,4,'3ème journée de votre escapade gourmande alsacienne','Petit déjeuner buffetVous profitez du spa de l''hôtel avant votre départ - soins non inclusEn option - Vous quittez Kaysersberg avec votre panier pique-nique préparé par le chef étoilé Olivier NastiJournée libre sur les routes du vignoble à la découverte des magnifiques villages alsaciens, du Château du Haut-Koenigsbourg, ...','https://www.vinotrip.com/img/cms/J3-Vignoble-Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (56,23,5,'Jour 1 - Initiation à la dégustation de Champagne','A votre arrivée en Champagne, vous prenez la direction de Boursault, à la Maison de Champagne Le Gallais,\xa0pour vivre une "masterclass 100% vin" : présentation des étapes de la vinification, visite des caves et démonstration de dégorgement à la volée, clôturée par une dégustation de 3 cuvées de la maison.\xa0En fin d''après-midi vous vous installez confortablement au Château de Rilly, une magnifique ancienne maison de champagne de la fin du 18ème siècle entourée des vignoblesDîner au restaurant gastronomique du Château de Rilly - Accord Mets et Champagnes en optionNuitée au Château de Rilly','https://www.vinotrip.com/img/cms/Fere_Programme.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (57,23,12,'Jour 2 - Golf et vie de Château en Champagne','Petit déjeuner au Château de RillyDépart pour le Golf de Reims pour un parcours 18 trousVous vous rendez ensuite à Reims pour une visite suivie d’une dégustation dans l’une des maisons de Champagne les plus renomméesVous terminez votre séjour par une visite libre de la ville de Reims','https://www.vinotrip.com/img/cms/golf-de-reims-champagne_030679_full.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (58,24,13,'Jour 1 - Arrivée au coeur du vignoble alsacien','A votre arrivée en Alsace, vous êtes attendus à Hunawihr, où vous avez rendez-vous pour une visite du domaine François Schwach et une dégustation de leurs spécialités de Crémants d’AlsaceVous vous rendez ensuite à Murbach, et vous vous installez au Schaeferhof, ancienne Métairie des Princes Abbés de Murbach bâtie au 18ème siècle et inscrite sur le Registre Supplémentaire des Monuments HistoriquesEn option - Diner à la Taverne du vigneron, restaurant traditionnel alsacien situé à Guebwiller, menu à la carte et boissons comprisesNuitée au Schaeferhof dans une des chambres d’hôtes feutrée et romantique','https://www.vinotrip.com/img/cms/Maison%20Schaeferhof.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (59,24,7,'Jour 2 - Balade sur la route des vins d''Alsace','Petit déjeunerEn option - Départ matinal pour un survol en montgolfière du massif des Vosges, du vignoble et de la plaine d’Alsace. Après la magie du vol, le retour sur terre est le moment du partage des émotions autour d’un petit déjeuner : viennoiserie, Crémant d’Alsace et certificats de baptême de l’air personnalisésDirection Wettolsheim, vous avez rendez-vous pour une visite du domaine Stentz-Buecher et une dégustation des vins de la propriété dans la cave des vieux millésimesEn option - Diner gastronomique au restaurant étoilé du Chambard, menu hors boissonNuitée au Schaeferhof','https://www.vinotrip.com/img/cms/degustation-barrique-alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (60,24,2,'Jour 3 - Visite de Colmar','Petit déjeunerVotre séjour se termine à Colmar, que vous visitez de façon insolite, en barque le long de la Lauch','https://www.vinotrip.com/img/cms/Lauch%20%C3%A0%20Colmar%20en%20Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (61,25,3,'Jour 1 - Arrivée à Riquewihr, sur la route des vins d''Alsace','A votre arrivée sur la route des vins d''Alsace, visite libre du village de Riquewihr, de sa beauté architecturale et de son site exceptionnel connus dans le monde entierEn fin d''après-midi, vous vous installez à la maison d''hôtes Demeure d''Antan, ancien domaine viticole datant du 18eme siècle situé entre Riquewihr et ColmarNuitée à la maison d''hôtes Demeure d''Antan','https://www.vinotrip.com/img/cms/Les%20remparts%20de%20Riquewihr.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (62,25,7,'Jour 2 - Dégustation de vins biologiques alsaciens','Petit déjeuner à la maison d''hôtesDépart pour la station du Lac Blanc pour un parcours de promenade atypique pieds nus pour profiter du sol naturel de la forêt environnante et de la variété de matériaux : sable, gravier, gravillons, pierres, dalles, bois, ...En descendant de la station, vous vous rendez à Beblenheim au domaine Bott-Geyl pour une visite et une dégustation de leurs magnifiques Grands Crus cultivés en biodynamie : Sonnenglanz, Furstentum, Schoenenbourg, Mandelberg, …En option - Diner à la winstub du Chambard, hors boisson, autour de plats typiques de la gastronomie alsacienne revisités par le chef étoilé Olivier Nasti : choucroute, backaoffa, escargots, munster, ...Nuitée à la maison d''hôtes Demeure d''Antan','https://www.vinotrip.com/img/cms/Martin%20Schaetzel%20vignes.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (63,25,15,'Jour 3 - Découverte de Colmar et de la viticulutre biologique','Petit déjeuner à la maison d''hôtesDécouverte de Colmar en barque le long de la Lauch, une façon insolite et écologique de visiter la villeEn option - Déjeuner à l''Arpège, premier restaurant biologique de Colmar - Menu hors boissonAprès le déjeuner, direction Wettolsheim, vous avez rendez-vous pour une visite et une dégustation des vins du domaine Stentz-Buecher où vous serez initiés aux pratiques culturales en agriculture biologique','https://www.vinotrip.com/img/cms/Lauch%20Colmar%20-Alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (64,26,3,'Jour 1 - Installation au domaine à Saint-Estèphe','A votre arrivée à Bordeaux, vous vous dirigez à Saint-Estèphe où vous vous installez au Château Ormes de Pez, un domaine viticole historique du Médoc, dans une splendide chartreuse du XVIIIème siècleDans l’après-midi, en immersion au sein de la magnifique propriété viticole Ormes de Pez, découvrez l’histoire du domaine et dégustez leur vin, un Cru Bourgeois exceptionnel exprimant l’authenticité du terroir de l’appellation Saint-EstèpheVous savourez un plateau repas maison accompagné d''une bouteille de vin du ChâteauNuitée au Château Ormes de Pez','https://www.vinotrip.com/img/cms/sejour-saint-estephe-hebergement.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (65,26,6,'Jour 2 - Dégustations de vins entre Pauillac et Saint-Estèphe','Petit déjeuner à votre hébergementEn option : vous êtes conviés au Château Lynch-Bages, Grand Cru Classée 1855 à Pauillac pour un cours de dégustation (uniquement du lundi au vendredi) ou bien participez à un atelier assemblage au Château Paloumey à Ludon-MédocDéjeuner au Café Lavinal dans le village de Bages, au cœur du vignoble de Pauillac - hors boissonAprès-midi sur la route des Châteaux du Médoc, vous vous arrêtez chez l''un de nos vignerons partenaires près de Saint-Estèphe pour une visite suivie d''une dégustation des vins du ChâteauEn option - Diner dans un restaurant à proximité du Château Ormes de Pez, où le chef propose une cuisine raffinée et savoureuse - Menu hors boissonNuitée au Château Ormes de Pez','https://www.vinotrip.com/img/cms/Jour%202%20-%20M%C3%A9doc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (66,26,13,'Jour 3 - Découverte du Médoc','Petit déjeuner à votre hébergementJournée libre sur les routes du Médoc à la découverte du "Fort Médoc", du "Fort Paté" et de la Citadelle de Blaye. Profitez-en également pour aller admirer le Château Margaux en empruntant le chemin de randonnée qui mène jusqu''au fleuve','https://www.vinotrip.com/img/cms/Jour%203%20-%20M%C3%A9doc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (67,27,5,'Vivre une expérience oenologique unique','Votre coffret cadeau à la découverte du vignoble de Bordeaux ou de Bourgogne comprend :- une bouteille de bienvenue offerte par les propriétaires à votre arrivée- une visite guidée suivie d''une dégustation de vin au coeur d''un domaine viticole- un diner (menu hors boisson)- une nuitée au milieu des vignes en chambre double- le petit-déjeuner','https://www.vinotrip.com/img/cms/coffret-experience-vin.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (68,27,10,'Commandez votre cadeau et partez quand vous voulez','Pour 2 personnes, le coffret cadeau est valable 18 mois à partir de la date d''achat.L''organisation du séjour est totalement prise en charge par VINOTRIP. Les bénéficiaires du cadeau choisissent eux-mêmes leur date de départ, leur destination (Bordeaux ou Bourgogne), leur hébergement et les propriétés viticoles qu''ils souhaitent visiter parmi notre sélection de domaines.Vous n''avez plus qu''à partir à la découverte d''un des plus beaux vignobles de France et rencontrer nos vignerons partenaires retenus pour la qualité et l''authenticité de leur prestation.','https://www.vinotrip.com/img/cms/coffret-sejour-vins.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (69,28,11,'Jour 1 - Découverte du vignoble Sancerrois','A votre arrivée à Cosne-sur-Loire, entre le Berry et la Bourgogne, vous vous installez dans une des délicieuses chambres du domaine du Prieuré Saint AgnanVous partez à la rencontre du domaine familial Eric Louis à Thauvenay, pour une visite commentée de la cave et des installations suivie d''une dégustation des AOC Sancerre, Pouilly Fumé, Menetou-salon, ... accompagnée de crottins de ChavignolVous poursuivez votre week-end oenologique en pays Sancerrois dans une chèvrerie, où vous découvrirez les secrets de fabrication du célèbre crottin de Chavignol, fromage star de la région, pour ensuite le déguster ! Si activité non disponible, bouteille de vin offerteAvant de dîner, profitez du magnifique jardin du prieuré pour prendre l’apéritif autour des spécialités régionales dégustées (et peut-être achetées...) l’après-midiVous dînez tout près de Cosne-Sur-Loire, au restaurant Le Chat proposant une cuisine créative et gourmande selon les envies du chef - Menu hors boisson, apéritif inclusNuit au Domaine du Prieuré Saint Agnan','https://www.vinotrip.com/img/cms/Week-end%20oenologique%20Sancerre.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (70,28,14,'Jour 2 - Visite d''une cave millénaire à Sancerre','Petit déjeuner au domaine du Prieuré Saint AgnanVous êtes attendus dans la matinée dans un domaine viticole, pour une visite des caves de calcaire, vieilles de plusieurs centaines d''années. Vous clôturez la visite par une dégustation commentée des vinsEn option - Déjeuner gastronomique dans un restaurant - Menu hors boissonAprès-midi libre à la découverte de Sancerre et des vignobles du Centre-Loire, de Pouilly, des Coteaux du Giennois, ...En option - Prolonger votre séjour d''une nuit supplémentaire au domaine du Prieuré Saint Agnan','https://www.vinotrip.com/img/cms/S%C3%A9jour%20oenologie%20Sancerre.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (71,29,15,'Jour 1 - Dégustation de Champagne et dîner gastronomique','A votre arrivée en Champagne, vous débutez votre séjour à Boursault, à la Maison de Champagne Le Gallais, par une masterclass (présentation des étapes de la vinification, visite des caves et démonstration de dégorgement à la volée, clôturée par une dégustation de 3 cuvées de la maison) suivie d''une initiation au sabrageDîner gastronomique dans l''Orangerie du Château d’Etoges - hors boissonNuitée au Château d’Etoges','https://www.vinotrip.com/img/cms/Ch%C3%A2teau-Etoge.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (72,29,7,'Jour 2 - Visite des caves d''une grande maison de Champagne','Petit déjeuner au Château d''EtogesUne heure de soins au Spa du Château d''Etoges (en option)Selon les goûts et les budgets, déjeuner Au Petit Comptoir, au cœur du quartier des Halles du Boulingrin, menu bistrot entrée, plat, dessert ou déjeuner gastronomique à L’Assiette Champenoise, restaurant 3 étoiles au Guide Michelin, hors boisson (en option)Vous achevez votre weekend par une visite des caves du domaine Vranken-Pommery Monopole suivie d''une coupe de Champagne.Visite libre de Reims','https://www.vinotrip.com/img/cms/cave-champagne-pommery.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (73,30,11,'Jour 1 - Balade le long de la Loire en voiture de collection','A votre arrivée à Angers, vous récupérez votre voiture de collection (Renault Dauphine 1960, Citroën Traction 1955, Peugeot 203 1958, Renault Caravelle 1965) et suivez votre carnet de route spécialement conçu pour les véhicules d''époque à la découverte du vignoble d''Anjou, ses vins, ses châteaux ! Vous pouvez également venir avec votre propre voitureVous suivez la Loire et profitez des paysages viticoles en direction de SaumurVous prenez ensuite part à une visite et dégustation dans les célèbres Caves de GrenelleDîner gastronomique - en optionNuitée dans un hébergement de charme à Saumur - catégorie confort ou luxe au choix','https://www.vinotrip.com/img/cms/Séjour%20oenologique%20en%20voiture%20de%20collection.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (74,30,13,'Jour 2 - Sillonnez la route des vins d''Anjou','Petit-déjeuner à votre hébergementVous débutez votre journée par une visite d''un site troglodyte, niché sur un coteau avec vue imprenable sur la Loire : vous participerez à un parcours oenologique dans une cave troglodyte à la découverte de la diversité des vins de la vallée de la LoireVous poursuivez votre route en direction du magnifique Château de Montreuil-Bellay pour une visite. Vous pourrez également y déguster les vins du domaine (appellations Saumur, cabernet d''Anjou, crémant de Loire, ..)Dîner gastronomique - en optionNuit à proximité des vignobles - catégorie confort ou luxe au choix','https://www.vinotrip.com/img/cms/Séjour%20en%20anjou%20en%20voiture%20de%20collection.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (75,30,8,'Jour 3 - Visite de Châteaux de la Loire','Petit-déjeuner à votre hébergementEn matinée, vous visitez le Château de Serrant et sa fabuleuse collection de meubles et d''objets d''artDans l''après-midi, au coeur du village de Savennières, vous dégusterez les vins du Domaine du Closel et profiterez, le temps d''une balade, du parc et des vignes du châteauVotre visite du vignoble d''Anjou en voiture de collection se termine à Angers avec la restitution de votre véhicule','https://www.vinotrip.com/img/cms/Séjour%20oenologie%20en%20voiture%20collection.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (76,31,3,'Jour 1 - Rencontrez un vigneron et dégustez ses vins','A votre arrivée en Alsace, vous vous rendez chez l’un de nos vignerons partenaires pour une dégustation privilégiée en leur compagnie. Ils vous feront découvrir leur passion et leurs vins, autour d’animations thématiques (une activité retenue en fonction des disponibilités aux dates choisies) :- Testez vos cinq sens au Domaine Spannagel Paul & Fils à Katzenthal.- Découvrez les secrets de l’accord parfait entre chocolats & Grands Crus au Domaine Freyburger à Ammerschwihr ou initiez-vous aux subtilités d’un accord fromages & Grands Crus au caveau Meyer-Krumb à Sigolsheim. - Plus insolite encore, profitez d’une séance de Qi Gong au milieu des vignes sur les hauteurs du Domaine Blanck Paul & Fils à Kientzheim, offrant une vue à couper le souffle.- Philippe Blanck du Domaine Blanck Paul & Fils vous apprendra également le rôle des sols sur le caractère d’un Grand Cru au cours d’une visite des paysages et des terroirs qui composent son domaine.- Jean-Jacques Dietrich vous livrera avec passion tous les secrets de l’art de la vinification au cœur de ses vignes, à Kaysersberg.- Les vins Kuehn à Ammerschiwhr ne font pas que se déguster, ils se racontent. Au cours d’une visite parsemée d’anecdotes et d’Histoire. Vous en profiterez pour descendre à 7 mètres sous terre, pour une dégustation exclusive à la bougie…- Au Domaine Pierre Adam à Ammerschwihr, l’art de la dégustation prend tout son sens : observer la robe, l’intensité d’une couleur, la transparence ou encore la brillance d’un vin, humer les arômes puis, goûtez…Dîner dans un restaurant typiquement Alsacien proposant une cuisine gourmande et de saison (choix de la catégorie de restaurant en option). Un menu en accord mets & Grands Crus vous sera proposé : 3 plats + 3 verres de Grands Crus.Nuitée en chambre double dans l''un de nos hôtels partenaires.','https://www.vinotrip.com/img/cms/Week-end-Grands-Crus-Kaysersberg-Domaine-Kuehn.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (77,31,15,'Jour 2 - Balade dans le vignoble alsacien','Petit déjeuner dans votre hôtel de charme.Journée libre sur la Route des Vins d''Alsace à la découverte de ses nombreux villages pittoresques.','https://www.vinotrip.com/img/cms/decouverte-grands-crus-alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (78,32,10,'Jour 1 - Pause vigneronne au cœur du Pays Cathare','A votre arrivée en pays Cathare entre Fitou et Corbières en début d''après-midi, vous partez à la rencontre du carignan. Jérôme Bertrand et sa femme Sabine vous feront découvrir le domaine Bertrand-Bergé en toute convivialité au cours d''une balade en 4X4 à travers leurs vignes. La visite est suivie d''une dégustation des vins de la propriétéEn fin d’après-midi, vous vous installez à Cascastel-des-Corbières dans votre chambre d’hôtes au domaine du Grand Guilhem. De charmantes attentions imaginées par Séverine et Gilles vous y attendent : chocolats et gourmandises régionales, bouteille d’un grand vin du domaine, …Vous partez à la rencontre du domaine Grand Guilhem avec Gilles qui vous fera visiter le vignoble et le chai, avant de vous initier à la dégustation de vins (théorie et pratique) et vous faire découvrir les vins biologiques du domaine en sa compagnieDîner dans un restaurant au cœur du village, proposant une cuisine traditionnelle et généreuse - Vins inclusAprès votre dîner, profitez du calme et de la beauté de ce village occitan en déambulant dans ses ruelles pavéesNuitée dans une chambre du domaine viticole Grand Guilhem','https://www.vinotrip.com/img/cms/We%20initiation%20dégustation%20vins%20languedoc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (79,32,13,'Jour 2 - Dolce vita et patrimoine occitan','Petit-déjeuner à votre hébergementEn option - En matinée, vous faites route jusqu’à l’abbaye de Fontfroide pour une étape sur la route des Corbières. Vous débutez par une dégustation privée dans la cave datant du 19ème et poursuivez par un déjeuner à la table de Fonfroide (entrée/plat/dessert avec un verre de vin). En début d''après-midi, profitez d''une visite libre de l''Abbaye (la Roseraie et les Jardins en terrasse)Vous clôturez votre séjour par une visite libre au cœur du Pays Cathare à Lagrasse, classé parmi les plus beaux villages de France. Son abbaye, ses halles du XIVe siècle, ses ruelles pittoresques, ses artisans et leur savoir-faire vous transporteront hors du temps','https://www.vinotrip.com/img/cms/Inititation%20dégustation%20vins%20en%20Languedoc.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (80,33,9,'Vivez des instants dégustation dans les vignobles','Votre coffret cadeau de découverte des vignobles de Bourgogne ou de Bordeaux comprend :- une bouteille de bienvenue offerte par les propriétaires à votre arrivée- une visite guidée suivie d''une dégustation de vin au coeur d''un domaine- une nuitée au milieu des vignes en chambre double- le petit-déjeuner','https://www.vinotrip.com/img/cms/vignoble-instant-degustation.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (81,33,2,'Comment profiter de votre coffret cadeau ?','Le week-end dégustation à offrir est valable pour 2 personnes durant 18 mois à partir de la date d''achat. L''organisation est totalement prise en charge par VINOTRIP. Le bénéficiaire du cadeau choisit lui-même sa date de départ, sa destination (en Bourgogne ou à Bordeaux), son hébergement et les vignerons qu''ils souhaitent rencontrer parmi les partenaires sélectionnés. Nous n''avons plus qu''à vous souhaiter un bon voyage !','https://www.vinotrip.com/img/cms/coffret-we-degustation.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (82,34,9,'Jour 1 - Balade dans le vignoble en compagnie d''un guide chevronné','A votre arrivée dans le Roussillon, vous avez rendez-vous en matinée avec votre guide œnologique pour une randonnée pédestre dans le vignoble de Collioure ou Banyuls, en suivant les criques de la Méditerranée. Au mieux des vignes en terrasse, vous partirez à la rencontre des terroirs et cépages de ce vignoble millénaire. Cette matinée conviviale s’achèvera dans un domaine viticole familial où vous rencontrerez un vigneron qui vous offrira une dégustation de ses crus.Vous pouvez poursuivre par une escale à la plage, à une dizaine de minutes seulement.Vous vous installez ensuite à Laroque des Albères, chez Côté Saisons. Vos hôtes Nicolas et Ingrid se feront un plaisir de vous accueillir dans leur très raffinée maison d’hôtes.Diner à la table d''hôte de l''hébergement autour d’une cuisine gourmande, où les produits de saison sont à l’honneur - Menu avec boissons.Nuitée dans votre chambre d’hôtes chez Côté Saisons.','https://www.vinotrip.com/img/cms/randonnee-vignoble-roussillon.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (83,34,14,'Jour 2 - Détente au coeur du terroir du Roussillon','Petit déjeuner chez Côté Saisons.En matinée, vous êtes attendus au Château Montana pour une visite suivie d’une dégustation des vins de la propriété en compagnie du propriétaire et vigneron, Patrick Saurel.Vous déjeunez au restaurant semi-gastronomique La Littorine à Banyuls - Menu hors boisson.Après votre déjeuner, profitez de la beauté du paysage pour une balade digestive le long de la plage.','https://www.vinotrip.com/img/cms/sejour-randonnees-vignoble-roussillon.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (84,35,9,'Jour 1 - Visite et dégustation exclusive au coeur du vignoble de Touraine','A votre arrivée, vous prenez possession de votre chambre enchanteresse dans le fabuleux Château de Chissay, petit joyau dans l''écrin du Val de LoireDans les jardins du château de Chenonceau, vous embarquez pour un vol privatisé à bord d''une montgolfière, de laquelle vous surplomberez les vignobles et les châteaux de la Loire, à quelques centaines de mètres d''altitude. Profitez de ce moment magique en sirotant une coupe de Vouvray servie à bord (en option)En fin de matinée, vous prenez la direction de Rochecorbon, pour un déjeuner dans un restaurant - hors boisson, apéritif inclusL''après-midi, vous êtes attendus au château Gaudrelle à Rochecorbon, propriété de la famille Montmousseau élaborant du Vouvray depuis plusieurs générations. Vous y découvrirez les secrets de l''élaboration des vins pendant votre visite de la cave du Château clôturée par une dégustation de 5 vins du domaine accompagnés de spécialités régionalesA votre retour au Château de Chissay, vous profitez d''un dîner gastronomique cuisiné par le Chef - hors boissonNuitée au Château de Chissay','https://www.vinotrip.com/img/cms/S%C3%A9jour%20romance%20Val%20de%20Loire.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (85,35,15,'Jour 2 - Escapade romantique sur la route des Châteaux de la Loire','Petit déjeuner au ChâteauEn fin de matinée, vous partez à la rencontre d''une famille de vignerons passionnés depuis 5 générations à Civray de Touraine, aux Caves du Père Auguste. Au programme : visite des caves de tuffeau creusées par l''arrière-arrière-grand-père et dégustation du nouvel AOC Touraine-Chenonceau. Une bouteille offerte pour prolonger le plaisir chez vousDéjeuner gastronomique à l''Auberge du bon Laboureur, restaurant 1 étoile au guide Michelin, hors boisson (en option)Vous visitez après votre repas le célèbre Château de Chenonceau, surnommé le Château des Dames (entrée non incluse)','https://www.vinotrip.com/img/cms/Week-end%20romantique%20Touraine.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (86,36,7,'Jour 1 - Découvrez le vignoble de Grignan','A votre arrivée en Drôme Provençale, direction le charmant village de Grignan, blotti au pied de son célèbre château. Vous vous installez dans l’une des charmantes chambres de la maison d’hôtes Le Clos de La Tuilière.En fin d’après-midi, vous avez rendez-vous au domaine de Grangeneuve pour une dégustation privilège\xa0: vivez un échange avec le sommelier du domaine qui animera cette dégustation avec des toasts d’accompagnement.En option - Activité en remplacement de la dégustation - Devenez un véritable œnologue en participant à un atelier privatif d’initiation à la dégustation au domaine de Grangeneuve. Durant 2 heures, vous serez initiés par un sommelier à la dégustation analytique professionnelle et découvrirez le vocabulaire du vin, ainsi que toutes les étapes de la dégustation sensorielle (visuelle, olfactive et gustative).Dîner dans un restaurant autour d’une cuisine de saison - menu hors boisson.Nuit au Clos de la Tuilière.','https://www.vinotrip.com/img/cms/hebergement-drome-provencale.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (87,36,15,'Jour 2 - Visitez la Drôme provençale entre vignes et lavande','Petit déjeuner.En matinée, vous avez rendez-vous dans le cadre enchanteur du Domaine de Montine, ancienne ferme du château et propriété viticole familiale depuis trois générations, pour une visite suivie d’une dégustation des vins du domaine.A l’heure du déjeuner, savourez les produits frais du terroir au cours d’un repas en accord mets et vins dans un restaurant.Profitez de votre séjour au cœur de la Drôme provençale pour vous balader librement dans les magnifiques champs de lavande à proximité de votre hébergement\xa0!Nuit au Clos de la Tuilière.','https://www.vinotrip.com/img/cms/visite-cave-grignan.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (88,36,3,'Jour 3 - Journée à Montélimar','Petit déjeuner.A l’heure du déjeuner, vous avez rendez-vous dans un restaurant situé à Montélimar, proposant une cuisine locale typiquement provençale.Terminez votre séjour par la découverte de la capitale du nougat, Montélimar.','https://www.vinotrip.com/img/cms/Sejour%20degustation%20drome.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (89,37,9,'Jour 1 - Arrivée en Anjou','A votre arrivée en Anjou, vous êtes attendus à Beaulieu sur Layon au Château Soucherie.','https://www.vinotrip.com/img/cms/week-end-degustation-vins-anjou.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (90,37,4,'Jour 1 - Dégustation des vins de l’appellation Savennières','Vous profitez d''une visite exclusive du Château Soucherie à la découverte du domaine, du vignoble et des chais en compagnie d''un sommelier professionnel.','https://www.vinotrip.com/img/cms/vins-reputes-anjou-loire.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (91,37,13,'Jour 2 - Découverte de l’appellation Saumur-Champigny','Petit déjeuner au Château Soucherie.','https://www.vinotrip.com/img/cms/degustation-vigneron-anjou.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (92,38,12,'Jour 1 - Découverte du vignoble champenois','A votre arrivée en Champagne, vous rejoignez votre hébergement à quelques kilomètres au nord d’Epernay, la maison d''hôtes du Champagne Philippe Martin, charmante chambre d’hôtes située dans le village de CumièresDécouvrez le Champagne Philippe Martin, propriété familiale depuis 1892, et leur savoir-faire traditionnel. La visite des caves est suivie d''une dégustation des meilleures cuvées de la maisonDiner au Bistrot le 7 autour d''une cuisine de marché - En option forfait boissons comprenant une flûte de Champagne à l’apéritif, 2 verres de vins, café et eaux minéralesNuitée en chambre d’hôtes au Champagne Philippe Martin','https://www.vinotrip.com/img/cms/offrir-week-end-champagne.JPG',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (93,38,3,'Jour 2 - Sur la route des grandes maisons de Champagne','Petit-déjeunerVous prendrez ensuite la direction des grandes maisons de Champagne d’Epernay, au Domaine de Castellane pour une visite guidée du domaine, du musée et de sa célèbre tour panoramique de 60m de haut qui vous offrira une vue imprenable sur la ville. La visite est suivie de la dégustation d''un champagne du domaineVisite libre d’Epernay : ne manquez pas la fameuse Avenue de Champagne où se succèdent les prestigieuses Maisons de champagne','https://www.vinotrip.com/img/cms/cadeau-weekend-champagne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (94,39,10,'Jour 1 - Arrivée en Occitanie','A votre arrivée dans l’Hérault, prenez la direction de Montady où vous êtes attendus à L’Orée Des Vignes. Vous vous installez dans cette superbe maison d’hôtes dans un ancien domaine viticole entièrement rénové. Un cadre enchanteur au cœur des vignesVous profitez en fin d’après-midi du jardin ombragé et de la piscineDîner à la table d’hôtes de votre hébergement, autour d’une cuisine régionale – Menu hors boissonNuitée dans la maison d’hôtes\xa0L’Orée Des Vignes','https://www.vinotrip.com/img/cms/hebergement-occitanie.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (95,39,7,'Jour 2 - Balade dans le vignoble de Saint-Chinian','Petit déjeuner à votre hébergementDans la matinée, vous prenez la route en direction du fameux vignoble de Saint-Chinian pour une visite suivie d’une dégustation au Clos Bagatelle, propriété familiale depuis 1623 où Christine et Luc Simon vous transmettront leur passionVotre journée continue par une balade au cœur du Parc Naturel Régional du Haut-Languedoc, vous offrant des points de vue à couper le souffleDîner gastronomique au restaurant Au Lavoir, proposant une cuisine raffinée dans un cadre intime - hors boissonNuitée dans la maison d’hôtes L’Orée Des Vignes','https://www.vinotrip.com/img/cms/vignoble-saint-chinian.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (96,39,4,'Jour 3 - Les vignes en bord de mer','Petit déjeuner à votre hébergementEn matinée, vous rejoignez le littoral méditerranéen et terminez votre week-end par une dégustation au Château Le Bouïs, magnifique domaine viticole à Gruissan offrant une vue imprenable sur la merEn option - Déjeuner dans un restaurant à proximité du Château Le Bouïs, à Gruissan','https://www.vinotrip.com/img/cms/gruissan-chateau-le-bouis.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (97,40,8,'Week-end oenologique à la découverte de la route des vins d''Alsace','Le coffret cadeau spécial route des vins d''Alsace pour 2 personnes comprend :- une bouteille de bienvenue offerte par les propriétaires à votre arrivée- une visite guidée suivie d''une dégustation de vin dans un domaine viticole- un dîner dans un restaurant (menu hors boisson) - une nuit dans un village typiquement alsacien, en chambre double - le petit-déjeuner','https://www.vinotrip.com/img/cms/degustation-route-vins-alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (98,40,10,'Coffret cadeau sur la route des vins d''Alsace','Ce week-end est l’occasion d’une magnifique découverte de la route des vins alsacienne, longue de plus de 170 kilomètres. Baladez-vous au cœur du vignoble, sur les hauteurs des Vosges, et admirez la plaine d’Alsace qui s’étend à perte de vue. Une halte chez l’un de nos vignerons partenaires vous initiera à la culture de la vigne et à la dégustation des vins alsaciens les plus caractéristiques. Et la nuit dans un des villages du vignoble d''Alsace vous fera définitivement fondre pour cette chaleureuse région.','https://www.vinotrip.com/img/cms/vignoble-alsacien.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (99,40,12,'Comment en profiter ?','Ce cadeau pour 2 personnes est valable sur une période de 18 mois à compter de la date d''achat.Les bénéficiaires peuvent choisir leur date de départ, nous organisons l''ensemble du séjour : ils n''ont plus qu''à partir à la découverte de ce fabuleux vignoble alsacien et profiter de cette belle expérience oenologique, à la rencontre de nos vignerons partenaires.','https://www.vinotrip.com/img/cms/village-route-vin-alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (100,41,15,'Un week-end découverte des vignobles du Val de Loire','Le coffret cadeau de découverte des vignobles et des vins de la Vallée de la Loire pour 2 personnes comprend :- une bouteille de bienvenue offerte par les propriétaires à votre arrivée- une visite guidée suivie d''une dégustation de vin au coeur d''un domaine viticole- un dîner - Menu hors boisson - une nuitée dans le vignoble ligérien, en chambre double - le petit-déjeuner','https://www.vinotrip.com/img/cms/restaurant-sur-loire.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (101,41,12,'Coffret cadeau unique en Val de Loire','Ce coffret cadeau est l’occasion de découvrir la diversité des terroirs et la richesse viticole du Val de Loire. Parcourez le vignoble d''Anjou, initiez-vous à la finesse et l’élégance des vins de Touraine ou encore découvrez les accords mets et vins en Sancerre. Choisissez votre destination parmi les différentes régions viticoles des pays de la Loire puis laissez-vous séduire par la beauté des paysages et la magie des rencontres œnologiques et gourmandes qui s’offrent à vous. Partez à la rencontre de vignerons chaleureux et passionnés qui vous parleront de leur région comme personne.','https://www.vinotrip.com/img/cms/domaine-tonneau-loire.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (102,41,11,'Comment en profiter ?','Le coffret week-end pour 2 personnes est valable 18 mois. Les bénéficiaires du cadeau choisissent eux-mêmes leur date de départ. Notre équipe se chargera de l''organisation du séjour de A à Z.','https://www.vinotrip.com/img/cms/decouverte-vignoble-loire.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (103,42,6,'Jour 1 - Arrivée dans le vignoble de Saint-Emilion','A votre arrivée à Saint-Emilion,vous vous installez dans l’une des magnifiques chambres d''une propriété viticole. Vous découvrez une demeure entourée de ses vignes, pleine de charme et de raffinement.En fin d''après-midi, profitez d''une visite du domaine, puis dégustez le vin produit sur la propriété.En soirée, vous êtes attendus au restaurant le Clos Mirande pour un dîner gastronomique à la découverte de saveurs subtiles - Menu hors boisson.Nuit.','https://www.vinotrip.com/img/cms/clos_de_la_barbanne.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (104,42,6,'Jour 2 - Journée golf au coeur du vignoble','Petit déjeuner à votre hébergement.En matinée, vous partez pour le Saint-Emilionnais Golf Club, situé à 10km du village de Saint-Emilion, pour un déjeuner au restaurant du club suivi d’un parcours 18 trous. Dans une vallée bordée de chênes et entourée par les vignes, un parcours d’exception vous attend, signé par l''un des meilleurs architectes au monde, l''américain Tom Doak. Laissez-vous emporter par la vue et profitez !Fin d''après-midi libre à Saint-Emilion, cité médiévale classée au Patrimoine Mondial de l''Unesco depuis 1999.Nuit.','https://www.vinotrip.com/img/cms/golf_saint_emilion.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (105,42,6,'Jour 3 - Découverte d''un Grand Cru Classé','Petit déjeuner à votre hébergement.Vous êtes attendus en matinée pour une visite du Château Soutard, Grand Cru Classé de Saint-Emilion, propriété de vingt-deux hectares, suivie d’une dégustation des Grands Vins du domaine.','https://www.vinotrip.com/img/cms/chateau_soutard.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (106,43,14,'Des soirées œnologiques d''exception','Olivier Poussier et Vinotrip ont imaginé ensemble une gamme de soirées œnologiques d''exception afin de vous faire découvrir les plus beaux accords mets et vins, dans des établissements de choix privatisés pour l''occasion.','https://www.vinotrip.com/img/cms/soiree-sommelier-paris.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (107,43,6,'Retour sur la soirée du 7 juillet 2016','Article et Photos','https://www.vinotrip.com/img/cms/soiree-vin-paris-pages.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (108,44,10,'Jour 1 - Parenthèse romantique en Côte Rôtie','A votre arrivée en Côte Rôtie, vous avez rendez-vous à Ampuis au Caveau du Château pour une dégustation commentée des magnifiques vins en appellations Condrieu, Côte-Rôtie, Gigondas et Vacqueyras des maisons de la Famille Guigal.Dans l’après-midi, direction le sud de Condrieu pour une visite et dégustation au Domaine Boissonnet. Parcourez la propriété et les caves voûtées où sont élevés leurs vins en barrique. Profitez d''un historique de la maison familiale datant du 16eme siècle avant de déguster les vins du domaine.En fin d’après-midi, vous déposez vos valises au Domaine de Clairefontaine ****, hôtel de charme niché au cœur d’un écrin de verdure de trois hectares.Dîner au restaurant gastronomique du Domaine de Clairefontaine, classé 1 étoile au Guide Michelin depuis 1993, où officie le Chef Philippe Girardon – Menu entrée / plat / fromage / dessert, hors boisson.Boisson en option, en accord mets & vins élaboré par Dominique Viala, sommelier du restaurant depuis 1992.Nuitée au Domaine de Clairefontaine ****.','https://www.vinotrip.com/img/cms/week-end-romantique-cote-rotie.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (109,44,1,'Jour 2 - En couple, sur la route des vignobles','Petit-déjeuner au Domaine de Clairefontaine ****.En matinée, vous profitez d’une visite libre de Vienne et de son riche patrimoine gallo-romain. La cité est classée parmi l’un des plus beaux sites archéologiques d’Europe.Pour le déjeuner, rendez-vous dans un restaurant traditionnel pour une découverte des spécialités régionales – hors boisson.','https://www.vinotrip.com/img/cms/sejour-amoureux-cote-rotie.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (110,45,6,'Jour 1 - Domaine Jacques Selosse','A votre arrivée en Champagne, vous longez la vallée de la Marne à flanc de coteaux jusqu’à Avize, au cœur de la Côte des Blancs. Vous êtes attendus à l’hôtel Les Avisés, accolé au Domaine Jacques Selosse et créé par Corinne et Anselme Selosse en 2011 : une grande maison bourgeoise de 10 chambres seulement.','https://www.vinotrip.com/img/cms/domaine-selosse-degustation.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (111,45,10,'Jour 2 - Domaine Jacquesson','Petit déjeuner aux Avisés.','https://www.vinotrip.com/img/cms/domaine-jacquesson-degustation.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (112,46,11,'Jour 1 - Rencontres oenologiques et gastronomiques à Saint-Amour','A votre arrivée dans le Beaujolais en début d’après-midi, vous êtes attendus au Château de La Chaize à Odenas, le plus grand des châteaux viticoles de la région,\xa0 pour une visite du chai historique suivie d’une dégustation de trois vins du domaine. Terminez votre visite par une balade libre jusqu’au Château pour admirer l’architecture signée Jules Hardouin-Mansart et le jardin à la française réalisé sur les plans d’André Le Nôtre.Après votre visite, vous rejoignez votre hébergement de charme, l’Auberge du Paradis, une magnifique maison bourgeoise en plein cœur du vignoble de Saint-Amour. Profitez de ce coin de sérénité ouvert sur les vignes et flânez au bord de la piscine.A l''heure du dîner, vous savourez une cuisine gastronomique élaborée au fil des saisons et au gré du marché, dans un restaurant 1 étoile au guide Michelin à proximité de votre hébergement.Nuit dans votre hébergement de charme.','https://www.vinotrip.com/img/cms/visite-domaine-odenas.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (113,46,13,'Jour 2 - Dégustation commentée et accords mets et vins à Fleurie','Petit déjeuner copieux à votre hébergement.En fin de matinée, vous avez rendez-vous au Château des Bachelards, pionnier de l’agriculture biologique et biodynamique à Fleurie, Moulin-à-Vent et Saint-Amour. Après avoir découvert les vignes et les chais du domaine, vous savourez des accords inédits et gourmands de petites bouchées avec les vins de la propriété, autour d’une dégustation commentée.En option - Déjeuner bistronomique dans un restaurant proposant une cuisine raffinée élaborée à base de produits frais (hors boisson).','https://www.vinotrip.com/img/cms/brouilly-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (114,47,9,'Jour 1 - Arrivée au Pays de l''Hermitage','A votre arrivée dans le vignoble de l’Hermitage dans l’après-midi, vous êtes attendus au Domaine de la Croix du Verre, au cœur de l’appellation Crozes-Hermitage, pour une visite du domaine situé à 200 mètres d’altitude, suivie d’une dégustation des vins en compagnie du propriétaire viticulteur.Le soir, vous descendez à Tain-l’Hermitage où l’on vous a réservé une table pour votre dîner dans un restaurant - vin inclusNuitée dans la maison d''hôtes, au Domaine de la Croix du Verre.','https://www.vinotrip.com/img/cms/vignoble-hermitage.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (115,47,4,'Jour 2 - Dégustations à la propriété et balades dans les vignes','Petit-déjeuner.En matinée, vous avez rendez-vous chez l''un de nos vignerons partenaires pour une visite suivie d’une dégustation de la gamme de vins du domaine en appellations Crozes-Hermitage, Hermitage et Cornas.Déjeuner au restaurant appartenant à la famille Paul Jaboulet Aîné, Vineum, situé au cœur de Tain-l’Hermitage pour un déjeuner en accord mets & vinsL’après-midi, partez à la découverte du paysage alentours au cours d’une balade de 3,5 kilomètres à travers les vignes du Pays de l’Hermitage – feuille de route sur demandeEn option - Dîner gastronomique dans un restaurant 1 étoile au Guide Michelin (menu hors boisson)Nuitée au Domaine de la Croix du Verre.','https://www.vinotrip.com/img/cms/visite-domaine-jaboulet.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (116,47,5,'Jour 3 - Sur les bords du Rhône','Petit-déjeuner.Visite libre de Tournon-sur-Rhône, blottie au cœur des vignobles et qualifiée de "sentinelle de la vallée du Rhône", faisant face à Tain-l’HermitageVous terminez votre séjour par un déjeuner au restaurant traditionnel Le Farfadet, à Tournon-sur-Rhône – Menu hors boisson','https://www.vinotrip.com/img/cms/tournon-sur-rhone.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (117,48,6,'Jour 1 - Dans les coulisses du vignoble','A votre arrivée en début d’après-midi, vous êtes attendus dans un domaine viticole à Chiroubles, près de la « capitale historique du Beaujolais ». Vous serez initiés aux pratiques culturales du domaine, visiterez la cave puis dégusterez les différents vins de la propriété : Régnié, Côte de Brouilly, Beaujolais-Villages Blanc et Morgon.En fin d’après-midi, vous rejoignez votre maison d’hôtes de charme surplombant les vignes.A l’heure du dîner, savourez une cuisine authentique et régionale à la table d’hôtes de votre hébergement – vin inclusNuitée dans votre maison d''hôtes.','https://www.vinotrip.com/img/cms/visite-domaine-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (118,48,11,'Jour 2 - Dégustations au domaine, sur les chemins du Beaujolais','Petit déjeuner.Vous vous rendez au Domaine du Château de la Valette au pied du Mont Brouilly pour une matinée en immersion à la propriété. Au programme, visite des chais puis dégustation des différentes cuvées de la maison.Déjeuner dans un restaurant à proximité proposant une cuisine originale et créative élaborée à base de produits frais – hors boissonAprès-midi libre sur les chemins du Beaujolais, à la rencontre de ses paysages viticoles vallonnés, de ses châteaux et villages médiévaux aux pierres dorées.','https://www.vinotrip.com/img/cms/visite-vignoble-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (119,49,14,'Jour 1 - Dégustations au domaine','A votre arrivée dans le Beaujolais, vous êtes attendus dans un domaine viticole du Beaujolais pour une visite et dégustation à l’issue de laquelle vous bénéficierez d’une bouteille offerte.Dans l''après-midi, vous rejoignez votre maison d''hôtes de charme située en plein coeur du vignoble Mâconnais et Beaujolais. Profitez de ce coin de sérénité ouvert sur les vignes.A l''heure du dîner, vous êtes attendus à la table de votre chambre d’hôtes autour d''une cuisine typiquement locale. Apéritif, entrée, plat, fromage, dessert, vin et café.Nuitée dans votre maison d''hôtes.','https://www.vinotrip.com/img/cms/domaine-viticole-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (120,49,12,'Jour 2 - Sur les chemins du vignoble Beaujolais','Petit déjeuner dans votre chambre d''hôtes.En matinée, vous prenez la route vers la colline de Brouilly, couverte de vignes et coiffée de sa célèbre chapelle. Rendez-vous chez Isabelle et Jean-Pierre Crespin, au Domaine du Château de la Valette, pour une visite du cuvage suivie d''une dégustation commentée des différentes appellations de la propriété.Enfilez ensuite vos chaussures de marche et suivez les conseils des propriétaires vignerons pour découvrir les sentiers alentours.','https://www.vinotrip.com/img/cms/maison-hote-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (121,50,3,'Jour 1 - Entre ville et vignobles','A votre arrivée en Alsace en début d’après-midi, direction le village typique de Wettolsheim au sud de Colmar pour une visite suivie d’une dégustation des vins du domaine Stentz Buecher, conduit en agriculture biologique depuis 2010.Dans l’après-midi, découvrez Colmar d’une manière relaxante et originale en profitant d’une balade commentée en barque le long de la Lauch. Vous découvrirez ainsi les maisons à colombages, le calme et la beauté de la petite Venise d’Alsace (de mars à octobre). Hors saison, profitez de la deuxième partie de votre après-midi pour découvrir le domaine et les vins d’un vigneron indépendant près de Rouffach.En fin de journée, installez-vous au Château-hôtel d’Isenbourg à Rouffach, bâtisse historique surplombant la plaine d''Alsace.A l’heure du dîner, vous restez au château et rejoignez le restaurant “Les Tommeries” où officie le Chef Matthias Lingelser – Menu de saison avec amuse-bouche, entrée, plat, fromage, dessert et mignardises (hors boissons).Nuitée au Château d’Isenbourg.','https://www.vinotrip.com/img/cms/week-end-chateau-alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (122,50,14,'Jour 2 - Sérénité au Château','Petit-déjeuner au Château d’Isenbourg.Dans la matinée, profitez du spa du Château et relaxez-vous au bord de la piscine, dans le jacuzzi, le sauna ou le hammam.En option - Pour plus de plaisir, complétez votre matinée de détente par un modelage aux huiles essentielles d’une cinquantaine de minutes avant de clôturer votre escapade alsacienne.','https://www.vinotrip.com/img/cms/spa-chateau-isenbourg-alsace.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (123,51,13,'Jour 1 - Green-fee au Pau Golf Club 1856','A votre arrivée à Pau, votre séjour débute par un parcours de golf. Le Pau Golf Club 1856 à Billère vous accueille sur son parcours 18 trous, par 69 sur 5314m, tracé par l’architecte Willy Dunn. Il est situé dans le prolongement du parc du château, en bordure du Gave de Pau, dans un cadre superbe face aux montagnes et au milieu d''arbres centenaires - licence française de golf obligatoire pour accéder au parcoursVous vous installez ensuite au Parc Beaumont - MGallery, hôtel 5* à PauSelon l’horaire confirmé, accès réservé et partagé au Spa Bambou (piscine intérieure chauffée de 15m de long, bain bouillonnant et sauna). Temps libre pour profiter de la salle de fitness avec vue ou pour parcourir, à votre guise, la cité royaleNuit à l’hôtel Parc Beaumont - MGallery en chambre Ville','https://www.vinotrip.com/img/cms/week-end-golf-pau.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (124,51,13,'Jour 2 - Découverte du vignoble de Jurançon','Petit-déjeuner buffet à l’hôtelA 15 minutes de la cité royale, rejoignez les coteaux de Jurançon, prestigieuse appellation du Sud-Ouest. Vous suivrez un itinéraire à travers les paysages magnifiques du piémont pyrénéen qui vous mènera chez 2 viticulteurs indépendants : rendez-vous avec les vignerons, visite des chais et dégustations','https://www.vinotrip.com/img/cms/week-end-vin-golf-pau.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (125,52,15,'Jour 1 - A votre arrivée en Touraine','A votre arrivée à Fontaine-Guérin, dans les environs d''Angers, vous récupérez votre carnet de route et suivez l''itinéraire spécialement conçu pour les véhicules d''époque à la découverte des vins du vignoble de Touraine ! Vous récupérez votre voiture de collection (Renault Dauphine 1960, Citroën Traction 1955, Peugeot 203 1958, Renault Caravelle 1965) - en optionVous profitez des paysages bucoliques le long de la Loire en direction de ChinonDécouvrez ensuite le Domaine de la Noblaie, et sa culture biologique, à travers une visite de ses vignes et une dégustation de ses vinsEn fin de journée, baladez-vous dans les rues médiévales de Chinon, et partez à la rencontre de sa forteresse.Diner gastronomique - en optionNuitée dans un hébergement de charme à Chinon – catégorie confort, ou luxe au Château de Marsay, au choix','https://www.vinotrip.com/img/cms/domaine-viticole-touraine_2.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (126,52,5,'Jour 2 - De Chinon à Vouvray, sillonnez la route des vins de Touraine','Petit-déjeuner à votre hébergementVous entamez la journée par la visite du Château du Rivau, construit au XVème Siècle, et ses jardins saisonniers.Vous poursuivez ensuite votre route jusqu’à Rochecorbon pour prendre part à une visite ainsi qu’à une dégustation de Vouvray au fameux Domaine BrédifDiner gastronomique - en optionNuitée dans un hébergement confort au Domaine des Bidaudières ; ou luxe au Château de la Bourdaisière - au choix','https://www.vinotrip.com/img/cms/chateau-du-rivau-touraine_2.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (127,52,7,'Jour 3 - Découverte d''un site troglodyte et de son musée','Petit-déjeuner à votre hébergementEn matinée, cap sur Bourgueil où vous profiterez d’une visite et dégustation des vins de la région, à La Cave du Pays de BourgueilVotre visite du vignoble de Touraine en voiture de collection se termine avec la restitution de votre véhicule','https://www.vinotrip.com/img/cms/d%C3%A9gustation-vin-touraine.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (128,53,8,'Jour 1 - Entre Gers et Béarn','Rendez-vous en matinée sur les terres d''Alain Brumont, figure emblématique de la région au coeur de la Gascogne en Madiran, pour une découverte de ses célèbres domaines : le Château Montus et son chai à barriques souterrain puis le Château Bouscassé, propriété familiale depuis des générations.Vous profitez d''une visite guidée des chais, des cuviers et des vignes face aux Pyrénées suivie d''une première dégustation de deux vins.Pour votre déjeuner, rendez-vous à la table du Château Bouscassé pour un repas en accord mets & vins autour de la gastronomie régionale.Dans l''après-midi, vous prenez la route en direction de Pau pour un temps libre dans la fameuse cité natale d''Henri IV avant de vous installer dans votre hôtel 4*.Nuitée dans votre hôtel 4* en chambre double.','https://www.vinotrip.com/img/cms/chateau-montus-madiran.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (129,53,10,'Jour 2 - Découverte du vignoble de Jurançon','Petit-déjeuner à l''hôtel.Muni de votre carnet de route, vous sillonnez les routes champêtres des coteaux de Jurançon.Au cours de votre journée, vous faites haltes chez deux vignerons indépendants, à la rencontre de béarnais passionnés par leurs terroirs : présentation des domaines, visite des chais et dégustation des célèbres vins blancs à la robe dorée, s''accordant à merveille avec les spécialités locales comme le fromage de brebis ou le foie gras !Fin de journée libre.','https://www.vinotrip.com/img/cms/vignoble-jurancon.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (130,54,7,'Jour 1 - Arrivée dans le vignoble des Graves','A votre arrivée à Martillac, à 20 minutes du centre-ville de Bordeaux, vous êtes attendus au Château Smith Haut Lafitte en plein cœur du vignoble de Graves, pour une visite complète du domaine suivie d’une dégustation des vins du Château.En fin de journée, vous vous installez aux Sources de Caudalie, hôtel 5* où vous pourrez découvrir votre chambre à la décoration unique, dont la vue donne sur les vignes du Château Smith Haut Lafitte ou sur le lac de l’hôtel.Le soir, dîner au restaurant La Table du Lavoir des Sources de Caudalie : le chef Nicolas Masse vous surprendra par ses menus créatifs et élaborés – menus hors boisson.Nuitée aux Sources de Caudalie.','https://www.vinotrip.com/img/cms/chateau-smith-haut-lafitte.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (131,54,15,'Jour 2 - Spa Vinothérapie','Petit-déjeuner aux Sources de Caudalie.Pendant la matinée, vous bénéficiez d’un moment de bien-être au Spa Vinothérapie des Sources de Caudalie. Laissez entre les mains expertes des vinothérapeutes, vous profitez pendant 50 minutes des soins de la maison. Alliant eau de source chaude et extraits de vignes et de raisin, ces soins exceptionnels sont reconnus pour leurs vertus anti-oxydantes uniques.Vous partez en début d’après-midi pour une balade à vélo dans les environs. Pour le déjeuner, emportez le panier repas que le chef Nicolas Masse vous aura concocté, afin de profiter d’un pique-nique gourmand en pleine nature - en option.','https://www.vinotrip.com/img/cms/sources-de-caudalie.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (132,55,3,'Jour 1 - Tout savoir sur l''Armagnac','Le Domaine à Lafitte vous reçoit pour une dégustation d''Armagnac, produit depuis 1945 par cette maison à partir des cépages Baco et Ugni Blanc. Découvrez les collections des millésimes et comparez leurs arômes. Vous visiterez également les chais de vieillissement.En fin d''après-midi, vous vous installez à La Cour des Saligues, un ancien relais de poste de 1850 transformé en magnifique chambre d''hôte au coeur de la Gascogne.Dîner à la table d''hôte autour de produits de saison - fait maison, menu en 3 plats avec un verre de vin.Nuit à La Cour des Saligues.','https://www.vinotrip.com/img/cms/gers-chambre-hote.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (133,55,2,'Jour 2 - Visite d''un domaine viticole et gourmandises du sud-ouest','Petit déjeuner à La Cour des Saligues.Visite du Château de Salles, un château du XVIIIème siècle : découvrez les chais étonnants semi-enterrés du XVIIème siècle et dégustez ensuite une sélection d''Armagnac et de Floc de Gascogne.Dîner dans un restaurant à proximité de votre hébergement, profitez d’un repas authentique autour des spécialités du sud-ouest - Menu, hors boissons.Nuit à La Cour des Saligues.','https://www.vinotrip.com/img/cms/degustation-armagnac-gers.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (134,55,11,'Jour 3 - Découverte des appellations viticoles des Pyrénées','Petit déjeuner à La Cour des Saligues.Visite des chais et dégustation au Domaine de Laougue, producteur des principaux AOC des Hautes-Pyrénées et Pyrénées-Atlantiques, le Madiran et le Pacherenc-du-Vic-Bilh - 2 bouteilles vous seront offertes à l’issue de la visite.Vous terminez votre séjour par un déjeuner à la Ferme aux cerfs et aux sangliers dans un parc animalier - menu en 3 plats hors boissons - suivi d''une promenade digestive dans la ferme où cerfs, sangliers et canards complètent un cadre champêtre.','https://www.vinotrip.com/img/cms/vignoble-gers.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (135,56,15,'Jour 1 - Entre paysages et saveurs à la Provençale','Votre séjour débute par une découverte de la richesse des vins locaux au Domaine du Deffends, producteur de vins 100% biologiques. Savourez le classique rosé et laissez-vous surprendre par l’intensité des vins rouges lors de la dégustation commentée à la caveEn fin d’après-midi, vous vous installez à La Dryade, au cœur du parc régional de la Sainte Baume, qui vous accueille dans un cadre calme et reposant. Au dîner, vos hôtes vous servent un repas préparé avec des produits locaux - Menu en 3 plats avec boissons Nuit à La Dryade','https://www.vinotrip.com/img/cms/week-end-bio-provence.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (136,56,8,'Jour 2 - Du vin au naturel','Petit déjeuner. Avant de partir, prenez votre panier pique-nique pour profiter d’un déjeuner en plein airEn matinée, rencontrez Fabienne de La Safranière de Mirandolier, produisant des safrans certifiés biologiques. Découvrez son travail dans ce terroir unique mais aussi ses idées de recettes. Un sac de safran vous est offert à la suite de votre visiteC’est l’heure du déjeuner, et vous êtes déjà muni de votre panier pique-nique ! Rejoignez les Gorges de Caramy\xa0: admirez la beauté naturelle du lieu, entre cascades et verdureVotre voyage continue au Château Lafoux dans le Parc Régional de la Sainte Baume, converti en bio depuis 2012 et engagé dans une démarche biodynamique. Dégustez une sélection de vins biologiques (rouges, blancs et rosés) et prenez le temps de vous promener le long des chemins de la propriété. Une bouteille vous est offerte à l’issue de votre dégustationAu diner, La Dryade vous accueille à nouveau à sa table d’hôtes pour un repas Provençal - Menu en 3 services avec boissons Nuit à La Dryade','https://www.vinotrip.com/img/cms/vins-biologiques-provence.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (137,56,8,'Jour 3 - L''art de la poterie de Provence','Petit déjeunerPour terminer votre séjour, vous êtes attendus au Pinceau Provençal, où vous rencontrez Maryse, artiste passionnée de poterie et de sa région. Découvrez son travail et la transformation précieuse de la terre à l’objet','https://www.vinotrip.com/img/cms/art-poterie-provence.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (138,57,13,'Jour 1 - Au pied de la Montagne Sainte-Victoire','A votre arrivée, visitez le Mas de Cadenet, qui produit parmi les plus beaux vins de Provence. Vous découvrez un terroir extraordinaire au pied de la montagne Sainte-Victoire, dans un domaine viticole dont l’histoire remonte à 1813. La visite suit le parcours du grain de raisin jusqu’à la bouteille de vin pour comprendre les étapes de la création des vins. De retour au caveau, vous dégustez une sélection de vins du domaine (AOP Sainte-Victoire et Côtes de Provence).En fin d’après-midi, vous vous installez dans votre chambre d’hôtes au cadre typiquement Provençal.Pour le dîner, vous êtes attendu à la Table du Beaurecueil, pour une cuisine de saisons. Dans un cadre élégant et chaleureux, vos hôtes vous proposent les meilleurs vins de la région pour accorder vos plats - Menu en 3 services, deux verres de vin par personne. Nuit à la chambre d''hôte de charme La Milane.','https://www.vinotrip.com/img/cms/week-end-gastro-provence.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (139,57,15,'Jour 2 - Découverte des vins et des huiles de Provence','Petit déjeuner.Partez à la découverte du domaine Terre de Mistral qui produit des vins et des huiles ! Vous découvrez le chemin surprenant de la terre au verre et les étapes d’élaboration de l’huile d’olive du domaine.A la fin de votre visite, vous participez à une initiation à la dégustation animée sous forme de jeux par votre guide : reconnaissance d’arômes et dégustation à l’aveugle.Vous profitez ensuite d’un déjeuner provençal à la ferme-auberge du domaine. Le tout avec une vue imprenable sur la montagne Sainte-Victoire ! Menu Gourmand en 3 services, hors boisson.Nuit à la chambre d''hôte de charme La Milane.','https://www.vinotrip.com/img/cms/degustation-vin-huile-provence.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (140,57,8,'Jour 3 - Des moments de détente','Petit déjeuner.Profitez des derniers instants dans votre hébergement pour vous détendre et profiter du cadre !','https://www.vinotrip.com/img/cms/chambre-hote-provence.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (141,58,2,'Jour 1 - Dégustations des vins de Bandol','A votre arrivée, visitez le domaine Bunan, qui produit de belles variétés des AOC Bandol. Vous êtes accueilli au caveau où votre hôte vous indique le parcours à suivre pour une balade autonome dans les vignes. Vous dégustez, de retour au caveau, une sélection de vins du domaine.Vous continuez sur les chemins du vignoble de Bandol et rencontrez David au domaine Lou Capelan, crée en 1992. Vous visitez la cave et dégustez les vins de l''AOC Bandol et de l''IGP Mont Caume et Var.Découvrez ensuite le village médiéval entouré de vignes La Cadière d’Azur sur votre route.En fin de l’après-midi, vous vous installez dans votre hôtel, l’Hostellerie Bérard 4*, au cœur du vignoble de Bandol. Ce superbe établissement est composé d’un ensemble de maisons au cœur du village\xa0: la Bastide aux Volets Bleus, le Pavillon des Peintres, le couvent du XIème siècle et les Remparts. Vous prenez place dans votre chambre et vous profitez des installations de l’hôtel (piscine extérieure, ...).Pour le diner, vous êtes attendus au restaurant de l’hôtel, une étoile au Guide Michelin, où vous dégustez le menu du marché - Menu en 3 services hors boisson.Nuit à l’Hostellerie Bérard.','https://www.vinotrip.com/img/cms/degustation-vins-bandol.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (142,58,5,'Jour 2 - Sur les chemins du vignoble de Bandol','Petit déjeuner à l’Hostellerie Bérard.En option - Dans la matinée, profitez au spa de votre hôtel de 2 heures de détente au sein d''un espace entièrement dédié au bien-être (sauna, hammam, fontaine de glace, bassin avec jet hydro-massant, cardio-training, tisanerie, ...)En option - Sur une demi journée, vous sillonnez les routes pittoresques au cœur des vignobles dans une voiture provençale mythique, la Méhari. Votre chauffeur guide vous emmènera à la rencontre de deux vignerons hauts en couleur et aux pratiques viticoles différentes. Suite à la visite des domaines, une dégustation vous sera proposée.A la fin de votre séjour, profitez des charmantes plages à proximité, entre ports de plaisance et villages typiques. Réservez toute l’année et partez entre avril et octobre, de mercredi à dimanche.','https://www.vinotrip.com/img/cms/sejour-vin-bandol.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (143,59,11,'Jour 1 - A votre arrivée à Cadaqués','A votre arrivée sur les hauteurs de Cadaqués, vous prenez possession de votre chambre au Mas Perafita, construit au XIVème siècle et restauré par la famille Martín Faixó qui a repris le vignoble de l''Alt Empordà au coeur du parc naturel du Cap de Creus, entre la mer et la montagneA l''heure du diner, rendez-vous au restaurant sur la promenade de Cadaqués autour d''une cuisine du marché et de la pêche du jour - Menu hors boissonNuitée en chambre double au Mas Perafita dans le domaine viticole Martin Faixo','https://www.vinotrip.com/img/cms/sejour-vin-cadaques.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (144,59,3,'Jour 2 - Découverte des vins d''Empordà','Petit déjeuner de produits frais et locaux à votre hébergement en profitant de la magnifique vue sur les montagnes alentoursVous poursuivez ensuite par une visite au Mas Perafita pour découvrir la tradition viticole familiale et les techniques de production du domaine Martín Faixó. La visite se termine par une dégustation des vinsDans l''après-midi, vous vous rendez au Mas Llunes qui produit des vins au caractère typique de l''Empordà. Vous visitez la cave et expérimentez une dégustation sensorielle (associant les arômes et couleurs du vin avec des jeux de musique et de lumière)Nuitée en chambre double au Mas Perafita dans le domaine viticole Martin Faixo','https://www.vinotrip.com/img/cms/degustation-vin-emporda.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (145,59,10,'Jour 3 - Promenade dans les vignes et le long du littoral','Petit déjeuner à votre hébergementProfitez de votre matinée libre pour vous balader au milieu des allées de vignes du parc naturel du Cap de Creus et vous promener le long de la mer à Cadaqués','https://www.vinotrip.com/img/cms/village-cadaques.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (146,60,11,'Jour 1 - Bienvenue en Empordà','A la frontière franco-espagnole, vous vous installez confortablement à votre hébergement, le charmant hôtel Can Xiquet, entouré par la nature et offrant une vue imprenable sur la plaine de l''alt Empordà et la mer MéditerranéeDiner au restaurant gastronomique de l''hôtel - Menu hors boissonNuitée à l''hôtel Can Xiquet','https://www.vinotrip.com/img/cms/hebergement-catalogne-emporda.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (147,60,15,'Jour 2 - Déjeuner en accord mets et vins au domaine','Petit déjeuner à votre hébergementVous êtes attendus à Masia Serra pour une visite du domaine viticole et de son vignoble, sur l''appellation EmpordàLa visite se poursuit par une dégustation des vins et des huiles d''olives autour d''un brunch mettant à l''honneur les produits régionaux (fromages, charcuteries locales artisanales, ...)Profitez de vos derniers moments pour partir à la découverte de la route des vins de l''Empordà','https://www.vinotrip.com/img/cms/degustation-route-vin-emporda.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (148,61,13,'Jour 1 - Vignoble de Saint Mont & Pau','Rendez-vous à la mi-journée à Saint-Mont, vignoble de caractère et charmant village du Gers dominé par son éperon rocheux. Initiation aux terroirs et cépages locaux en parcourant la parcelle de vigne d’un grand vin, visite du chai à la découverte des secrets de vinification et d’élevage puis, dans la magnifique cave voûtée du Monastère perché, dégustation commentée de 4 vins d’appellations régionales (Madiran, Saint Mont, Pacherenc du Vic-Bilh, …) associés à 4 copieuses tapas de saison, façon Sud-Ouest, élaborées par le Chef du restaurant gastronomiqueDans l''après-midi, vous prenez la route en direction de Pau pour vous installer à votre hôtel 4*Temps libre pour découvrir la cité royale, à votre guise, et notamment son superbe belvédère sur les PyrénéesNuit à l''hôtel','https://www.vinotrip.com/img/cms/vin-tapas-sud-ouest.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (149,61,4,'Jour 2 - Découverte du vignoble de Jurançon','Petit déjeuner à l''hôtelA 15 minutes du centre-ville, rejoignez les coteaux de Jurançon. Sec ou moelleux, ce délicieux vin à la robe dorée est entré dans l’Histoire avec Henri IV en 1533, lors du baptême du futur roi de France. Vous suivrez un itinéraire à travers les paysages magnifiques du piémont pyrénéen qui vous mènera chez deux viticulteurs indépendants de l’appellation : rencontre avec les vignerons, visite des chais et dégustations.','https://www.vinotrip.com/img/cms/vignoble-jurancon-visite.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (150,62,13,'Jour 1 - Dégustation de Saké','Votre séjour débute par une visite de la première brasserie française de Saké, Les Larmes du levant, à Pélussin. Grégoire, le fondateur, vous accueille pour une visite commentée. Vous découvrez la fabrication de cet alcool japonais fermenté à base de riz. La visite est suivie par une dégustation des sakés - une bouteille vous est offerte à l’issue de votre dégustationProlongez votre découverte du Saké au cours d’un dîner au restaurant gastronomique l’Ode Marine (spécialités poissons et fruits de mer). Le chef Christophe Boucher, grand connaisseur de l’art culinaire japonais après 6 années d’expérience au sein de restaurants gastronomiques au Japon, conçoit pour vous un menu en accords mets et Saké. L’alliance harmonieuse entre gastronomie française et japonaise\xa0!Nuit à la chambre d’hôtes de charme A la belle histoire','https://www.vinotrip.com/img/cms/fabrication-sake-sejour.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (151,62,5,'Jour 2 - Journée Zen','Petit déjeuner à votre hébergementMatinée immersive dans une atmosphère japonaise à l’occasion d''une visite libre d’un Jardin Zen d’Erik Borja, une référence dans l''art des jardins d''inspiration japonaise en France. Sur presque 4 hectares de surface, plusieurs jardins le composent\xa0: jardin d''accueil, de méditation, de thé, de promenade et du Dragon','https://www.vinotrip.com/img/cms/jardin-zen-rhone.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (152,63,12,'Jour 1 - Arrivée dans le Beaujolais, découverte des vins','A votre arrivée dans la région, vous êtes attendus à Villié-Morgon, chez un vigneron indépendant, pour une visite du domaine suivie d’une dégustation des vins de la propriété. L’occasion de s’immerger dans l’univers Beaujolais.Déjeuner dans un restaurant autour d''un repas régional à la découverte des saveurs locales - hors boisson (en option).Dans l’après-midi, vous vous installez dans votre magnifique chambre au Château de Pizay ****.Une fois installés, rendez-vous à l’œnothèque du château pour effectuer un parcours œnosensoriel. Composé de 8 ateliers ludiques, ce parcours vous apprendra tout ce qu’il y a à savoir sur les arômes, les saveurs, les tanins, le vieillissement du vin… A la fin de ce parcours vous pourrez mettre vos connaissances en pratique lors d’une dégustation au rez-de-jardin.Pour le dîner, une table vous est réservée au restaurant gastronomique du Château. L’occasion de découvrir une cuisine actuelle composée grâce aux meilleurs produits de la région sélectionnés par le chef lui-même. – menus hors boisson.Nuitée au Château de Pizay.','https://www.vinotrip.com/img/cms/romance-viticole-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (153,63,7,'Jour 2 - Journée bien-être au Château','Petit-déjeuner au Château de Pizay.Journée libre, vous bénéficiez d’un accès à l’espace Aquasens d''une durée de 2h, qui vous permettra de découvrir le bassin ludique d’hydrothérapie et l’espace multi sensoriel (sauna, hammam, douche cascade, douche à expérience...).','https://www.vinotrip.com/img/cms/spa-amoureux-beaujolais.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (154,64,1,'Jour 1 - Arrivée dans le Jura','Dans l’après-midi, vous vous installez confortablement à votre hôtel restaurant 3 étoiles Logis Le Bois Dormant, dans un cadre paisible et naturel sur la route des vins du JuraVous avez un accès illimité au complexe détente. Profitez de la piscine, du jacuzzi, sauna, hammam ainsi que du terrain de tennis ou encore du parc arboré de 6 hectares selon vos envies\xa0!Dîner au restaurant de l’hébergement - Macvin d’accueil, demi-bouteille de vin du Jura pour 2 et café comprisNuit à l’hôtel Le Bois Dormant en chambre double','https://www.vinotrip.com/img/cms/sejour-jura.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (155,64,15,'Jour 2 - Découverte des spécialités jurassiennes','Petit déjeuner en buffet à votre hébergementEn matinée, vous participez à une visite guidée des Salines de Salins les Bains, inscrite au Patrimoine mondial de l’UNESCODéjeuner au restaurant Des Deux Forts à Salins les BainsDans l’après-midi, direction Poligny pour une visite de la Maison du Comté afin de tout savoir sur le fromage emblématique de la régionProlongez votre découverte des saveurs jurassiennes par une dégustation de vins chez un vigneronVotre journée gourmande se termine par un dîner gastronomique au Bois Gourmand - demi-bouteille de vin du Jura pour 2 et café comprisNuit à l’hôtel Le Bois Dormant en chambre double','https://www.vinotrip.com/img/cms/degustation-vin-jura.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (156,64,11,'Jour 3 - Dégustation de Comté','Petit déjeuner en buffet à votre hébergementA l’issue de votre séjour, 500g de comté vous sont offerts dans une fruitière pour que vous puissiez continuer à vous régaler chez vous\xa0!','https://www.vinotrip.com/img/cms/visite-jura.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (157,65,2,'Le Domaine la Bouche du Roi','L’histoire du domaine remonte à 1588 avec la réunion de sommeliers et d’échansons, appelés la Bouche du Roi, chargés de sélectionner et garantir la qualité des vins servis à Versailles. Le domaine de la Bouche du Roi, en clin d’œil au passé viticole de l’Île-de-France, entend donc faire revivre leur savoir-faire\xa0!','https://www.vinotrip.com/img/cms/visite-oenologie-yvelines.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (158,65,7,'Parcours oenotouristique avec accord bouchées signatures','Découverte du village historique de Davron et du parc de la Plaine de VersaillesVisite du vignoble de la Bouche du Roi et sa collection de cépages raresDégustations des vins de la propriété en accord avec les produits locaux achetés en circuit court','https://www.vinotrip.com/img/cms/parcours-oenotourisme-ile-de-france.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (159,66,10,'Jour 1 - Découvertes de Grands Crus Classés de Graves','Vous débutez votre séjour par la visite du Château Carbonnieux, Grand Cru Classé de Graves. Découvrez les moments forts de l’histoire de la propriété, le vignoble et son terroir spécifique, visitez les chais et découvrez la « mémoire » du Château, le caveau où dort plus d''un siècle de millésimes. La visite se termine par une dégustation des vins « Grand Cru Classé » (rouge et blanc) issus de l’agriculture raisonnableVous poursuivez par une visite privée et guidée du Château Haut-Bailly, Cru Classé de Graves. Au cours d''un passage dans les vignes et les chais, vous apprenez les secrets du savoir-faire de la maison et profitez d’une dégustation commentée des vins de la propriété accompagnée d’une ardoise gourmande (fromages affinés, ...)En fin d''après-midi, vous vous installez au Château Le Pape, magnifique maison d’hôtes appartenant au Château Haut-Bailly, située sur le terroir historique de l’appellation Pessac-Léognan. Profitez de la quiétude des lieux au cours d’une balade à vélo (selon disponibilités) ou à pieds dans le vignoble environnantDîner au restaurant La Table du Lavoir – Menu en 3 services, hors boissonNuitée au Château Le Pape','https://www.vinotrip.com/img/cms/piscine-chateau-le-pape.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (160,66,10,'Jour 2 - Visite et dégustation au Château Pape Clément','Petit déjeuner au Château Le PapeVous avez rendez-vous au Château Pape Clément situé à Pessac et considéré comme l’un des plus anciens Grands Crus de Bordeaux. Vous visitez le cuvier bois dédié à la vinification et le chai à barriques où repose le vin pendant 18 mois, et découvrez les secrets de fabrication et d’élevage de ce Grand Cru Classé de Graves. Votre visite se termine par une dégustation de 3 vins de différents terroirs, dont le Château Pape Clément rouge','https://www.vinotrip.com/img/cms/graves-chateau-pape-clement.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (161,67,5,'Accueil au domaine de la Bouche du Roi à Davron, dans les Yvelines','Vivez une expérience œnologique inédite au\xa0domaine de la Bouche du Roi, à Davron dans les Yvelines.','https://www.vinotrip.com/img/cms/atelier-vin-montreuil.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (162,67,12,'Atelier œnologique : assemblez et créez votre propre cuvée !','Visite du vignoble en compagnie de votre guideApprentissage des différentes étapes de la dégustationParticipation à un atelier d’assemblage animé par une équipe de spécialistes pour créer le vin qui vous ressemble à partir des cépages du domaine.Vous repartez ensuite avec votre bouteille personnalisée !','https://www.vinotrip.com/img/cms/creer-cuvee.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (163,68,1,'Jour 1 - Arrivée dans la Vallée du Lot, entre vignes et châteaux','Rendez-vous au Château de Haute-Serre, véritable institution viticole à Cahors, célèbre pour ses Malbecs. La visite de la propriété et des chais est suivie d’une dégustation d’une sélection des vins du domaine.En fin d’après-midi, vous vous installez dans l’une des magnifiques chambres du majestueux Château de Mercuès, construit au XIIème siècle au cœur de la vallée du Lot. Vous profitez d’une visite du Château et de ses imposantes caves, suivie d’une dégustation de 3 vins de la propriété, où l’on cultive la vigne depuis 1983.Dîner gastronomique au restaurant étoilé du Château Le Duèze. Des déclinaisons subtiles et des mariages de saveurs audacieux dans un cadre magique – Menu en 4 services, hors boisson.Nuit au Château de Mercuès.','https://www.vinotrip.com/img/cms/we-cahors.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (164,68,10,'Jour 2 - Dégustations de vins régionaux en AOC Cahors','Petit déjeuner au Château de Mercuès.En matinée, profitez des installations du château. Des vélos sont à votre disposition pour découvrir les alentours.L’après-midi, rendez-vous au domaine du Théron pour une découverte du chai et une dégustation des vins de Cahors et du cépage Malbec qui le caractérise.Votre séjour se termine par une visite libre de Cahors, ville d’art et d’histoire de par son architecture médiévale.','https://www.vinotrip.com/img/cms/we-degustation-cahors.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (165,69,1,'Jour 1 - Sur la route des vins du Jura','Votre séjour débute dans l’après-midi par une découverte libre Arlay, Cité de Caractère de Bourgogne-Franche-Comté et Capitale du Vin de Paille par la qualité de son vignoble et la renommée de son nectarLa visite se poursuit par une dégustation des vins du Jura, dans une cave du XVIème siècle où le vigneron vous présentera son métier et son choix de la culture en biodynamieDîner gourmand en 3 services accompagné d’un apéritif régional, deux vins en accord avec les plats, une coupe de crémant (eau minérale et café inclus)Nuit à l’hôtel en chambre supérieure','https://www.vinotrip.com/img/cms/vins-jura-degustation.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (166,69,3,'Jour 2 - Visite des plus beaux villages jurassiens','Petit déjeuner à votre hébergementDépart à la découverte de Château-Chalon, un des Plus Beaux Villages de France.Vous vous rendez au musée de la Maison de la Haute Seille et complétez votre visite par une dégustation commentée de 3 vins typiques, notamment le fameux Vin Jaune Château ChalonVisite libre de Baume-les-Messieurs, un village niché dans un cirque aux confins de trois valléesVotre journée se termine par un dîner en accords mets vins. Menu en trois services - apéritif régional, 3 cépages de vins, coupe crémant, café et eau minérale inclusNuit à l’hôtel en chambre supérieure','https://www.vinotrip.com/img/cms/village-jura-baume-les-messieurs.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (167,69,5,'Jour 3 - Départ du Jura','Petit déjeuner à votre hébergementCrédit photos : © Stéphane Godin/Jura Tourisme','https://www.vinotrip.com/img/cms/vignoble-jura-chateau-chalon.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (168,70,13,'Jour 1 - Entre dégustations et évasion à L’Abbaye de la Celle','A votre arrivée en Provence, votre séjour commence par une halte au Château Lafoux. Vous dégustez une sélection de vins de la propriété. Une bouteille vous est offerte à l’issue de la visite Vous vous installez ensuite à l’hostellerie de L’Abbaye de la Celle, splendide demeure qui vous accueille dans un cadre aussi élégant que chaleureux. Profitez des espaces qui invitent à la détente aussi bien dans le jardin que dans les salons à l’intérieur, le tout au cœur des vignobles des Coteaux VaroisAu dîner, vous êtes attendus au restaurant étoilé de l’hôtel, appartenant à la Maison Alain Ducasse. Le chef vous surprend avec une cuisine de saison élaborée avec les produits du potager de l’Abbaye - Menu de saison avec apéritif et un accord mets et vins (entrée, deux plats, fromage et dessert)Nuit à l’Hostellerie de L’Abbaye de la Celle','https://www.vinotrip.com/img/cms/hebergement-provence-verte.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (169,70,5,'Jour 2 - Découverte œnologique de l’Appellation des Coteaux-Varois-en-Provence','Petit déjeuner à L’Abbaye de la CelleEn matinée, vous participez à un atelier dégustation à la Maison des vins des Coteaux Varois. Cette association regroupe les principaux producteurs de la région, et vous accueille pour une initiation à la dégustation des vins de cette appellation. Découvrez les spécificités des Coteaux Varois en Provence et visitez le conservatoire. Participez enfin d’un atelier initiation à la dégustation. Admirez ensuite les très nombreux cépages qui y sont cultivés et présentés en jardin à la françaiseProfitez de la fin de votre séjour pour parcourir la route des vins de Provence en toute autonomie. Visitez aussi le village de Tourves et admirez son architecture gothique Provençale','https://www.vinotrip.com/img/cms/we-provence-verte.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (170,71,13,'Jour 1 - Dégustations raisonnées à Pau','A votre arrivée à Pau dans l’après-midi, vous vous installez à l’hôtel et partez explorer la capitale des Pyrénées.Rendez-vous en fin de journée dans une cave-bar à vin du centre-ville pour y déguster des produits locaux et des vins issus d’agriculture biologique ou biodynamique. Le sommelier vous guidera pour trouver des vins à votre goût qui accompagneront une copieuse planche de charcuteries et de fromages variés des Pyrénées - 2 verres de vin inclus.Nuit en chambre double dans un hôtel 4* de l’agglomération paloise.','https://www.vinotrip.com/img/cms/cave-bar-vin-pau.jpg',NULL,NULL);
INSERT INTO t_e_etape_etp (etp_id, sjr_id, hbg_id, etp_titre, etp_description, etp_photo, etp_url, etp_video) VALUES (171,71,8,'Jour 2 - Réflexions savoureuses en Jurançon Bio','Petit déjeuner à l''hôtel.En option - Location d’un vélo à assistance électrique pour la demi-journée afin de privilégier un déplacement écologique et ainsi limiter votre empreinte carbone\xa0!Rendez-vous dans la matinée au Domaine Latapy dans le vignoble de Jurançon à proximité directe de Pau. Ce domaine, en viticulture biologique depuis 20 ans, vous accueille par un escape game qui vous entraine dans une enquête policière à la découverte de la bibliothèque de feu Lord Beckett.Une fois l’énigme résolue (ou pas), votre guide Irène vous présentera le domaine, son terroir, ses vignes et ses pratiques culturales biologiques puis vous proposera une dégustation commentée et détaillée de ses vins.Vous clôturez votre séjour savoureux au Domaine Latapy par un déjeuner bio végétarien fait maison - boissons incluses.','https://www.vinotrip.com/img/cms/degustations-raisonnees-jurancon.jpg',NULL,NULL);
