INSERT INTO t_e_avis_avi (avi_id, clt_id, sjr_id, avi_note, avi_commentaire, avi_titreavis, avi_dateavis) VALUES (96,47,5,5,'Accueillis à la Maison Prosper Maufoux par Pascale, maîtresse de maison absolument divine et chaleureuse, dîner au restaurant le restaurant Le Terroir avec un service digne d''un gastro, visite tres instructive des vignes avec la maison Olivier Leflaive, dégustation Au Domaine DEBRAY... Au delà de la qualité des prestations, nous avons fait des rencontres humaines inoubliables. Ce week end fut enrichissant à souhait. Merci Vinotrip','Week-end enrichissant','08/04/2017');
INSERT INTO t_e_avis_avi (avi_id, clt_id, sjr_id, avi_note, avi_commentaire, avi_titreavis, avi_dateavis) VALUES (97,22,5,5,'Un ensemble de hautes prestations en commençant par la Maison Olivier Leflaive, une table d''hôtes et une superbe dégustation !!! Une chambre d''hôtes chez Prosper Maufoux nettement supérieure à un hôtel 3 étoiles !!! Et pour finir un passage au Domaine Debray avec un accueil très professionnel. Un séjour totalement réussi à conseiller vivement !','Séjour en couple le 10 et 11 juin 2017','06/12/2017');
