INSERT INTO t_e_avis_avi (avi_id, clt_id, sjr_id, avi_note, avi_commentaire, avi_titreavis, avi_dateavis) VALUES (100,49,5,5,'Séjour très intéressant et instructif, avec de très bons accueils. Ambiance très sympa. Beaucoup de passion dans ce métier, surtout sur Santenay, chambre et restaurant, et dans la petite cave de Beaune....qui a retenue toute notre attention. Très beau séjour. Merci à Vinotrip pour l''organisation....','Séjour découverte','09/21/2016');
